class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return "9" * n

        if k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"

        if k == 3:
            return "9" * n

        if k == 4:
            if n <= 2:
                return "8" * n

            left = int("8" + "9" * ((n - 2 + 1) // 2))

            for _ in range(10):
                if n % 2 == 0:
                    c = int(str(left) + str(left)[::-1])
                else:
                    c = int(str(left) + str(left)[::-1][1:])
                if c % 4 == 0:
                    return str(c)
                left -= 1

            left = int("88" + "9" * ((n - 4 + 1) // 2))
            for _ in range(10):
                if n % 2 == 0:
                    c = int(str(left) + str(left)[::-1])
                else:
                    c = int(str(left) + str(left)[::-1][1:])
                if c % 4 == 0:
                    return str(c)
                left -= 1

            return "8" * n

        if k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n - 2) + "5"

        if k == 6:
            left = int("8" + "9" * ((n - 2 + 1) // 2))
            while True:
                if n % 2 == 0:
                    c = int(str(left) + str(left)[::-1])
                else:
                    c = int(str(left) + str(left)[::-1][1:])
                if c % 6 == 0:
                    return str(c)
                left -= 1

        if k == 8:
            if n <= 2:
                return "8" * n

            left = int("8" + "9" * ((n - 2 + 1) // 2))
            for _ in range(10):
                if n % 2 == 0:
                    c = int(str(left) + str(left)[::-1])
                else:
                    c = int(str(left) + str(left)[::-1][1:])
                if c % 8 == 0:
                    return str(c)
                left -= 1

            left = int("88" + "9" * ((n - 4 + 1) // 2))
            for _ in range(10):
                if n % 2 == 0:
                    c = int(str(left) + str(left)[::-1])
                else:
                    c = int(str(left) + str(left)[::-1][1:])
                if c % 8 == 0:
                    return str(c)
                left -= 1

            left = int("888" + "9" * ((n - 6 + 1) // 2))
            for _ in range(10):
                if n % 2 == 0:
                    c = int(str(left) + str(left)[::-1])
                else:
                    c = int(str(left) + str(left)[::-1][1:])
                if c % 8 == 0:
                    return str(c)
                left -= 1
            return "8" * n

        if k == 9:
            return "9" * n

        # k == 7
