            return "7"

        left = int("9" + "9" * ((n - 2 + 1) // 2))
        while True:
            if n % 2 == 0:
                c = int(str(left) + str(left)[::-1])
            else:
                c = int(str(left) + str(left)[::-1][1:])
            if c % 7 == 0:
