import math

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def ans(n, d):
            if d == 1:
                return "9" * n
            if d == 2:
                if n == 1:
                    return "8"
                if n == 2:
                    return "88"
                else:
                    return "8" + "9" * (n - 2) + "8"
            if d == 3:
                return "9" * n
            if d == 4:
                if n <= 4:
                    return "8" * n
                else:
                    return "8" * 2 + "9" * (n - 4) + "8" * 2
            if d == 5:
                if n <= 2:
                    return "5" * n
                else:
                    return "5" + "9" * (n - 2) + "5"
            if d == 6:
                if n <= 2:
                    return "6" * n
                if n % 2 == 1:
                    if n == 3:
                        return "8" * n
                    else:
                        k = math.ceil((n - 3) / 2)
                        return "8" + "9" * k + "8" + "9" * k + "8"
                else:
                    if n == 4:
                        return "8" + "77" + "8"
                    else:
                        k = math.ceil((n - 5) / 2)
                        return "8" + "9" * k + "77" + "9" * k + "8"
            if d == 7:
                if n <= 2:
                    return "7" * n
                else:
                    if n % 2 == 1:
                        for p in range(9, 0, -1):
                            s = (n // 2) * '9' + str(p) + (n // 2) * '9'
                            if is_divisible_by_7(s):
                                return "9" * (n // 2) + str(p) + "9" * (n // 2)
                    else:
                        for p in range(9, 0, -1):
                            s = ((n - 1) // 2) * '9' + str(p) + str(p) + ((n - 1) // 2) * '9'
                            if is_divisible_by_7(s):
                                return "9" * ((n - 1) // 2) + str(p) + str(p) + "9" * ((n - 1) // 2)

            if d == 8:
                if n <= 6:
                    return "8" * n
                else:
                    return "8" * 3 + "9" * (n - 6) + "8" * 3
            if d == 9:
                return "9" * n

        return ans(n, k)

def is_divisible_by_7(s: str) -> bool:
    num = 0
    for digit in s:
        num = (num * 10 + int(digit)) % 7
    return num == 0
