class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # return (9999990999999%7 == 0)
        # ans = 0
        # for i in range(10**(n), -1, -1):
        #     if i%k == 0 and str(i) == str(i)[::-1]:
        #         return i
        # return ans
        if k == 1:
            return "9"*n
        if k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + "9"*(n-2) + "8"
        if k == 3:
            return "9"*n
        if k == 4:
            if n < 4:
                return "8"*n
            return "88" + "9"*(n-4) + "88"
        if k == 5:
            if n == 1:
                return "5"
            return "5" + "9"*(n-2) + "5"
        if k == 6:
            if n <= 2:
                return '6'*n
            if n == 3:
                return "888"
            if n == 4:
                return "8778"
            if n % 2 == 0:
                return "8" + "9"*((n-4)//2) + "77" + "9"*((n-4)//2) + "8"
            else:
                return "8" + "9"*((n-3)//2) + "8" + "9"*((n-3)//2) + "8"
        if k == 7:
            if n == 1:
                return "7"
            if n == 2:
                return "77"
            prevN = n
            n = prevN%24+24
            if n % 2 == 0:
                for i in range(99, -1, -1):
                    s = "9"*((n-2)//2) + str(i) + "9"*((n-2)//2)
                    if int(s) % k == 0 and s == s[::-1]:
                        # return s
                        # return "9"*((prevN-2)//2) + str(i) + "9"*((prevN-2)//2)
                        s = "9"*((prevN-2)//2) + str(i) + "9"*((prevN-2)//2)
                        return s
                        # return int(s) % k == 0 and s == s[::-1]
            else:
                for i in range(9, -1, -1):
                    s = "9"*((n-1)//2) + str(i) + "9"*((n-1)//2)
                    if int(s) % k == 0 and s == s[::-1]:
                        # return s
                        s = "9"*((prevN-1)//2) + str(i) + "9"*((prevN-1)//2)
                        return s
                        # return int(s) % k == 0 and s == s[::-1]
        if k == 8:
            if n <= 6:
                return "8"*n
            return "8"*3 + "9"*(n-6) + "8"*3
        if k == 9:
            return "9"*n
