class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """
        if n == 1:
            for i in range(9, 0, -1):
                if i%k == 0:
                    return str(i)

        if n == 2:
            for i in range(99, 0, -1):
                if i%k == 0 and str(i) == str(i)[::-1]:
                    return str(i)

        if n == 3:
            for i in range(999, 0, -1):
                if i%k == 0 and str(i) == str(i)[::-1]:
                    return str(i)

        if n == 4:
            for i in range(9999, 0, -1):
                if i%k == 0 and str(i) == str(i)[::-1]:
                    return str(i)

        if n == 5:
            for i in range(99999, 0, -1):
                if i%k == 0 and str(i) == str(i)[::-1]:
                    return str(i)

        if k in [1, 3, 9]:
            return '9'*n

        if k == 2:
            return '8' + '9' * (n-2) + '8'
        if k == 4:
            return '88' + '9' * (n-4) + '88'
        if k == 8:
            return '888' + '9' * (n-6) + '888'

        if k == 5:
            return '5' + '9' * (n-2) + '5'

        if k == 6:
            if n % 2:
                m = n//2
                return '8' + '9' * (m-1) + '8' + '9' * (m-1) + '8'
            else:
                m = n//2
                return '8' + '9' * (m-2) + '77' + '9' * (m-2) + '8'

        for i in range(9, -1, -1):
            if n%2:
                s = '9'*(n//2) + str(i) + '9'*(n//2)
            else:
                s = '9'*(n//2 - 1) + str(i)*2 + '9'*(n//2 - 1)
            if int(s) % 7 == 0:
                return s
