class Solution {
public:

    bool check(string s){
        int c=0;
        int n=s.size();
        for(int i=0;i<n;i++){
            c = (c*10+s[i]-'0')%7;
        }
        return c==0;
    }
    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1 || k==3 || k==9){
            for(int i=0;i<n;i++){
                ans +='9';
            }
        }
        else if(k==2){
            if(n<=2){
                for(int i=0;i<n;i++){
                    ans +='8';
                }
            }
            else{
                ans ="8";
                for(int i=0;i<n-2;i++){
                    ans +='9';
                }
                ans +='8';
            }
        }
        else if(k==4){
            if(n<=4){
                for(int i=0;i<n;i++){
                    ans +='8';
                }
            }else{
                ans = "88";
                for(int i=0;i<n-4;i++){
                    ans +='9';
                }
                ans +="88";
            }
        } else if(k==5){
            if(n<=2){
                for(int i=0;i<n;i++){
                    ans +='5';
                }
            }
            else{
                ans ="5";
                for(int i=0;i<n-2;i++){
                    ans +='9';
                }
                ans +='5';
            }
        } else if(k==6){
            if(n==1){
                ans ="6";
            } else if(n==2){
                ans = "66";
            } else if(n==3){
                ans = "888";
            } else {
                if(n % 2 ==0){
                    ans ="8";
                    //
                    for(int i=0;i<n/2-2;i++){
                        ans +='9';
                    }
                    ans +="77";
                    //
                    for(int i=0;i<n/2-2;i++){
                        ans +='9';
                    }
                    ans +='8';
                } else{
                    ans ="8";
                    //
                    for(int i=0;i<n/2-1;i++){
                        ans +='9';
                    }
                    ans +='8';
                    //
                    for(int i=0;i<n/2-1;i++){
                        ans +='9';
                    }
                    ans +='8';
                }

            }
        } else if(k==7){
              if(k==1){
                ans = "7";
              }
              else if(k==2){
                ans = "77";
              }
              else{
                if(n%2 ==0){
                    string pre="";
                    for(int i=0;i<n/2-1;i++){
                        pre +='9';
                    }

                    for(int i=9;i>=0;i--){
                        string tmp ="";
                        tmp +=char(i+'0');
                        tmp +=char(i+'0');
                        if(check(pre+tmp+pre)){
                            ans = pre +tmp+ pre;
                            break;
                        }
                    }
                }else {
                    string pre="";
                    for(int i=0;i<n/2;i++){
                        pre +='9';
                    }
                    for(int i=9;i>=0;i--){
                        string tmp = "";
                        tmp +=char(i+'0');
                        if(check(pre+tmp+pre)){
                            ans = pre+tmp+pre;
                            break;
                        }
                    }
                }
              }
        } else if(k==8){
            if(n<=6){
                for(int i=0;i<n;i++){
                    ans +='8';
                }
            }else{
                ans = "888";
                for(int i=0;i<n-6;i++){
                    ans +='9';
                }
                ans +="888";
            }            
        }
        return ans;
    }
};
