import java.math.BigInteger; 
class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuilder ans = new StringBuilder();
        if (k==1 || k==3 || k==9) {
            for (int i=0; i<n; i++) {
                ans.append("9");
            }
            return ans.toString();
        }

        if (k==2) {
            if (n==1) return "8";
            ans.append("8");
            for (int i=1; i<n-1; i++) {
                ans.append("9");
            }
            ans.append("8");
            return ans.toString();
        }

        if (k==4) {
            if (n==1) return "8";
            if (n==2) return "88";
            if (n==3) return "888";
            ans.append("88");
            for (int i=2; i<n-2; i++) {
                ans.append("9");
            }
            ans.append("88");
            return ans.toString();
        }

        if (k==8) {
            if (n==1) return "8";
            if (n==2) return "88";
            if (n==3) return "888";
            if (n==4) return "8888";
            if (n==5) return "88888";
            ans.append("888");
            for (int i=3; i<n-3; i++) {
                ans.append("9");
            }
            ans.append("888");
            return ans.toString();
        }

        if (k==5) {
            if (n==1) return "5";
            ans.append("5");
            for (int i=1; i<n-1; i++) {
                ans.append("9");
            }
            ans.append("5");
            return ans.toString();
        }
        if (k==6) {
            if (n==1) return "6";
            else if (n==2) return "66";
            else if (n==4) return "8778";
            else if (n%2==1) {
                ans.append("8");
                for (int i=1; i<n/2; i++) {
                    ans.append("9");
                }
                return ans.toString()+"8"+ans.reverse().toString();
            } else {
                ans.append("8");
                for (int i=1; i<n/2-1; i++) {
                    ans.append("9");
                }
                return ans.toString()+"77"+ans.reverse().toString();
            }
        }
        if (k==7) {
            if (n==1) return "7";
            if (n==2) return "77";
            for (int i=0; i<(n-1)/2; i++) {
                ans.append("9");
            }
            for (int i=9; i>3; i--) {
                String ans1 = "";
                if (n%2==1) ans1=ans.toString()+Integer.toString(i)+ans.reverse().toString();
                else ans1=ans.toString()+Integer.toString(i*11)+ans.reverse().toString();
                BigInteger b = new BigInteger(ans1);
                BigInteger div = new BigInteger("7");
                //System.out.println(b.remainder(div));
                if (b.remainder(div).intValue()==0) return ans1;
            }
        }
        // 7 77 959 9779 99799 999999 9994999 99944999 
        // 7 77  5 77  7 99  4 44  6 44  4 99
        return "";
    }
}
