#include <string>
#include <vector>
#include <algorithm>
#include <iostream>

class Solution {
public:
    std::string largestPalindrome(int n, int k) {
        switch (k) {
            case 1:
            case 3:
            case 9: {
                std::string s(n, '9');
                return s;
            }
            case 2: {
                if (n == 1) return "8";
                std::string s = "8" + std::string(n - 2, '9') + "8";
                return s;
            }
            case 4: {
                if (n == 1) return "8";
                if (n == 2) return "88";
                if (n == 3) return "888";
                std::string s = "88" + std::string(n - 4, '9') + "88";
                return s;
            }
            case 5: {
                if (n == 1) return "5";
                std::string s = "5" + std::string(n - 2, '9') + "5";
                return s;
            }
            case 8: {
                if (n == 1) return "8";
                if (n == 2) return "88";
                if (n == 3) return "888";
                if (n == 4) return "8888";
                if (n == 5) return "88888";
                std::string s = "888" + std::string(n - 6, '9') + "888";
                return s;
            }
            case 6: {
                if (n == 1) return "6";
                if (n == 2) return "66";
                if (n == 3) return "888";
                std::string s = "8";
                if (n % 2 == 0) {
                    s += std::string(n / 2 - 2, '9') + "77" + std::string(n / 2 - 2, '9');
                } else {
                    s += std::string(n / 2 - 1, '9') + "8" + std::string(n / 2 - 1, '9');
                }
                s += "8";
                return s;
            }
            case 7: {
                if (n == 1) return "7";
                if (n == 2) return "77";
                for (int i = 9; i >= 0; --i) {
                    std::string s((n + 1) / 2 - 1, '9');
                    if (n % 2 == 0) {
                        s += std::to_string(i) + std::to_string(i);
                    } else {
                        s += std::to_string(i);
                    }
                    s += std::string((n + 1) / 2 - 1, '9');
                    if (test7(s)) return s;
                }
            }
        }
        return "";
    }

private:
    bool test7(const std::string& str) {
        int c = 0;
        for (char ch : str) {
            int s = ch - '0';
            c = (c * 10 + s) % 7;
        }
        return c == 0;
    }
};
