class Solution {
public:

    int isdivisible7(string num)
{
    int n = num.length(), gSum=0;
    if (n == 0)
        return 1;

    // Append required 0s at the beginning.
    if (n % 3 == 1) {
        num="00" + num;
        n += 2;
    }
    else if (n % 3 == 2) {
        num= "0" + num;
        n++;
    }

    // add digits in group of three in gSum
    int i, GSum = 0, p = 1;
    for (i = n - 1; i >= 0; i--) {

        // group saves 3-digit group
        int group = 0;
        group += num[i--] - '0';
        group += (num[i--] - '0') * 10;
        group += (num[i] - '0') * 100;

        gSum = gSum + group * p;

        // generate alternate series of plus
        // and minus
        p *= (-1);
    }

    return (gSum % 7 == 0);
}

    string check4(int n) {
        string ans = "";
        if (n <= 4) {
            for (int i = 0; i < n; i++) {
                ans += '8';
            }
        } else {
            ans += "88";
            for (int i = 0; i < n - 4; i++) {
                ans += '9';
            }
            ans += "88";
        }
        return ans;
    }

    string check8(int n) {
        string ans = "";
        if (n <= 6) {
            for (int i = 0; i < n; i++) {
                ans += '8';
            }
        } else {
            ans += "888";
            for (int i = 0; i < n - 6; i++) {
                ans += '9';
            }
            ans += "888";
        }
        return ans;
    }

    string check5(int n) {
        string ans = "";
        if (n == 1)
            ans += '5';
        else if (n == 2)
            ans += "55";
        else {
            ans += '5';
            for (int i = 0; i < n - 2; i++) {
                ans += '9';
            }
            ans += '5';
        }
        return ans;
    }

    string check6(int n) {
        string ans = "";
        if (n == 1)
            ans += '6';
        else if (n == 2)
            ans += "66";
        else if (n % 2) {
            int temp = (n - 3) / 2;
            ans += '8';
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            ans += '8';
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            ans += '8';
        } else {
            int temp = (n - 4) / 2;
            ans += '8';
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            ans += "77";
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            ans += '8';
        }
        return ans;
    }

    string check7(int n) {
        string ans = "";
        if (n == 1)
            ans += '7';
        else if (n == 2)
            ans += "77";
        else if (n % 2) {
            int temp = n / 2;
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            ans += '1';
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            for (int k = 9; k >= 1; k--) {
                ans[temp] = (char)(k + '0');
                if (isdivisible7(ans)) {
                    break;
                }
            }
        } else {
            int temp = n / 2;
            temp--;
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            ans += "11";
            for (int i = 0; i < temp; i++) {
                ans += '9';
            }
            for (int k = 9; k >= 1; k--) {
                ans[temp] = (char)(k + '0');
                ans[temp + 1] = (char)(k + '0');
                if (isdivisible7(ans)) {
                    break;
                }
            }
        }
        return ans;
    }

    string largestPalindrome(int n, int k) {
        string ans = "";
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) {
                ans += '9';
            }
        } else if (k == 2) {
            if (n == 1)
                ans = '8';
            else if (n == 2)
                ans += "88";
            else {
                ans += '8';
                for (int i = 0; i < n - 2; i++) {
                    ans += '9';
                }
                ans += '8';
            }
        } else if (k == 4) {
            ans = check4(n);
        } else if (k == 5) {
            ans = check5(n);
        } else if (k == 6) {
            ans = check6(n);
        } else if (k == 7) {
            ans = check7(n);
        } else if (k == 8) {
            ans = check8(n);
        } 
        return ans;
    }
};
