public class Solution {
    public string LargestPalindrome(int n, int k) {
        StringBuilder ans = new();
        for (int i = 0; i < n; ++i) ans.Append(9);
        switch (k) {
            case 2:
                ans[0] = ans[n-1] = '8';
                break;
            case 5:
                ans[0] = ans[n-1] = '5';
                break;
            case 6:
                if (n == 1) ans[0] = '6';
                else if (n == 2) ans[0] = ans[1] = '6';
                else if (n % 2 == 0) {
                    ans[0] = ans[n-1] = '8';
                    ans[n/2] = ans[n/2-1] = '7';
                } else {
                    ans[0] = ans[n-1] = '8';
                    ans[n/2] = '8';
                }
                break;
            case 4:
                if (n == 1) ans[0] = '8';
                else if (n == 2) ans[0] = ans[1] = '8';
                else {
                    ans[0] = ans[n-1] = ans[n-2] = ans[1] = '8';
                }
                break;
            case 8:
                if (n <= 4) for (int i = 0; i < n; ++i) ans[i] = '8';
                else ans[0] = ans[n-1] = ans[n-2] = ans[n-3] = ans[1] = ans[2] = '8';
                break;
            case 7:
                while (!check()) {
                    --ans[n/2];
                    if (n % 2 == 0) --ans[n/2-1];
                }
                break;
            case 1:
            case 3:
            case 9:
                break;
        }
        return ans.ToString();

        bool check() {
            int t = 0;
            for (int i = 0; i < n; ++i) {
                t += (ans[i] - '0');
                t %= 7;
                t *= 10;
            }
            return t == 0;
        }
    }
}
