class Solution {
public:
    char dp[100001][7];
    char dp_non_zero[100001][7];
    int nxt_mod[100001][7];
    int non_zero_nxt_mod[100001][7];

    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }

        if (k == 7) {
            if (n == 1) {
                return "7";
            }
            fill(&dp[0][0], &dp[0][0] + sizeof(dp), 0);
            fill(&dp_non_zero[0][0], &dp_non_zero[0][0] + sizeof(dp_non_zero), 0);

            for (int i = 1; i < 10; ++i) {
                dp[1][i % 7] = '0' + i;
                if (i != 0) {
                    dp_non_zero[1][i % 7] = '0' + i;
                }
            }

            int base_mod = 10 % 7;
            for (int i = 0; i <= 100000 - 2; ++i) {
                for (int j = 0; j < 7; ++j) {
                    if ((i == 0 && j != 0) || (i > 0 && dp[i][j] == 0)) {
                        continue;
                    }
                    for (int d = 9; d >= 0; --d) {
                        int new_mod = (base_mod * d + d + j * 10) % 7;
                        if (dp[i + 2][new_mod] == 0 || dp[i + 2][new_mod] < '0' + d) {
                            dp[i + 2][new_mod] = '0' + d;
                            nxt_mod[i + 2][new_mod] = j;
                        }
                        if (d == 0) continue;
                        if (dp_non_zero[i + 2][new_mod] == 0 || dp_non_zero[i + 2][new_mod] < '0' + d) {
                            dp_non_zero[i + 2][new_mod] = '0' + d;
                            non_zero_nxt_mod[i + 2][new_mod] = j;
                        }
                    }
                }
                base_mod = (base_mod * 10) % 7;
            }

            int q = n;
            string front(1, dp_non_zero[q][0]);
            string back(1, dp_non_zero[q][0]);
            int mod = non_zero_nxt_mod[q][0];
            q -= 2;

            while (q) {
                front += dp[q][mod];
                if (q > 1) {
                    back += dp[q][mod];
                } else {
                    break;
                }
                mod = nxt_mod[q][mod];
                q -= 2;
            }

            reverse(back.begin(), back.end());
            return front + back;
        }

        if (k == 5) {
            if (n == 1) {
                return "5"; // Special case when n is 1
            }
            string result(n, '9');
            result[0] = '5';
            result[n - 1] = '5';
            return result;
        }

        if (k == 4) {
            if (n <= 3) {
                return string(n, '8');
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            result[1] = '8';
            result[n - 2] = '8';
            return result;
        }

        if (k == 8) {
            if (n <= 5) {
                return string(n, '8');
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            result[1] = '8';
            result[n - 2] = '8';
            result[2] = '8';
            result[n - 3] = '8';
            return result;
        }

        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n == 3) {
                return "888";
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            if (n % 2 == 1) {
                result[n / 2] = '8';
            } else {
                result[n / 2] = '7';
                result[(n / 2) - 1] = '7';
            }
            return result;
        }

        if (k == 2) {
            if (n == 1) {
                return "8"; 
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            return result;
        }



        return "";
    }
};
