func largestPalindrome(n int, k int) string {
    switch {
	case k == 1 || k == 3 || k == 9:
		return strings.Repeat("9", n)
	case k == 2:
		{
			if n == 1 {
				return "8"
			}
			return "8" + strings.Repeat("9", n-2) + "8"
		}
	case k == 4:
		if n == 1 {
			return "8"
		}
		if n == 2 {
			return "88"
		}
		if n == 3 {
			return "888"
		}
		return "88" + strings.Repeat("9", n-4) + "88"
	case k == 5:
		{
			if n == 1 {
				return "5"
			}
			return "5" + strings.Repeat("9", n-2) + "5"
		}
	case k == 6:
		if n == 1 {
			return "6"
		}
		if n == 2 {
			return "66"
		}
		if n == 3 {
			return "888"
		}
		if n%2 == 1 {
			return "8" + strings.Repeat("9", (n-2)/2) + "8" + strings.Repeat("9", (n-2)/2) + "8"
		} else {
			return "8" + strings.Repeat("9", (n-3)/2) + "77" + strings.Repeat("9", (n-3)/2) + "8"
		}
	case k == 7:
		switch n % 12 {
		case 1, 5:
			return strings.Repeat("9", (n-1)/2) + "7" + strings.Repeat("9", (n-1)/2)
		case 2, 4:
			return strings.Repeat("9", (n-2)/2) + "77" + strings.Repeat("9", (n-2)/2)
		case 3:
			return strings.Repeat("9", (n-1)/2) + "5" + strings.Repeat("9", (n-1)/2)
		case 6, 0:
			return strings.Repeat("9", n)
		case 7, 11:
			return strings.Repeat("9", (n-1)/2) + "4" + strings.Repeat("9", (n-1)/2)
		case 8, 10:
			return strings.Repeat("9", (n-2)/2) + "44" + strings.Repeat("9", (n-2)/2)
		case 9:
			return strings.Repeat("9", (n-1)/2) + "6" + strings.Repeat("9", (n-1)/2)
		}
		return ""
	case k == 8:
		if n < 6 {
			return strings.Repeat("8", n)
		}
		return "888" + strings.Repeat("9", n-6) + "888"

	}
	return ""
}
