class Solution:
    sys.set_int_max_str_digits(10**5)
    def largestPalindrome(self, n: int, k: int) -> str:
        def isPalid(s):
            return s == s[::-1]
        # for n digits
        # k = 1 -> 99999
        # k = 2 -> 89998
        # k = 3 -

        if k == 1 or k == 3 or k == 9: return "9" * n
        elif k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + "9" * (n - 2) + "8"
        elif k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n - 4) + "88"
        # k = 1 8
        # k = 2 88
        # k = 3 888
        # k = 4 8888
        # k = 5 88988
        # k = 6 889988
        #  8899988



        elif k == 5:
            if n == 1:
                return "5"
            else:
                return "5" +  "9" * (n - 2) + "5"
        elif k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                 return "66"
            elif n == 3:
                return "888"
            elif n == 4:
                return "8778"
            else:
                if n % 2 == 1:
                    subn = (n - 1) // 2
                    return "8" + "9" * (subn - 1) + "8" + "9" * (subn - 1) + "8"
                else:
                    subn = (n-2) // 2 
                    return "8" +  "9" * (subn - 1) + "77" + "9" * (subn - 1) + "8"

        # n = 1 6
        # n = 2 66
        # n = 3 888
        # n = 4 8778
        # n = 5 89 8 98
        # n = 6 89 77 98
        # n = 7 899 8 998
        # n = 8 899 77 998
        # n = 9 899989998


        elif k == 7:
            # 959
            # 9779
            # 99799
            # 999999
            # 9994999
            # 99944999
            # 999969999
            # 9999449999
            if n == 1:
                return "7"
            elif n == 2:
                return "77"
            else:
                if n % 2 == 1:
                    # in the middle
                    # sys.set_int_max_str_digits()
                    for d in range(9, 0, -1):
                        cur_s = "9" * ((n - 1) // 2) + str(d) + "9" * ((n - 1) // 2)
                        if int(cur_s) % 7 == 0:
                            return cur_s
                else:
                    for d in range(9, 0, -1):
                        cur_s = "9" * ((n - 2) // 2) + str(d) * 2 + "9" * ((n - 2) // 2)
                        if int(cur_s) % 7 == 0:
                            return cur_s
        elif k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "8" * 3 + "9" * (n - 6) + "8" * 3
        #     if n == 1:
        #         return "8"
        #     elif n == 2:
        #         return "88"
        #  888
        #  8888
        # 88888
        # n = 7 8889888
        # n = 8 88899888
        # 
        else:
            min_n = int("1" + (n - 1) * "0")
            max_n = int("9" * n)
            dmax = (max_n // k) + 1
            dmin = (min_n // k)  - 1
            for d in range(dmax, dmin -1, -1):
                cur = k * d
                if cur >= min_n and cur <= max_n and isPalid(str(cur)):
                    print(str(cur))
                    return str(cur)
