class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1||k==3||k==9){
            string ans="";
            for(int i=1;i<=n;i++)ans+='9';
            return ans;
        }
        if(k==2){
            string ans="";
            for(int i=1;i<=n;i++){
                  if(i==1||i==n)ans+='8';
                  else ans+='9';
            }
            return ans;
        }
        if(k==4){
            string ans="";
            for(int i=1;i<=n;i++){
                  if(i==1||i==n||i==2||i==n-1)ans+='8';
                  else ans+='9';
            }
            return ans;
        }
        if(k==5){
            string ans="";
            for(int i=1;i<=n;i++){
                  if(i==1||i==n)ans+='5';
                  else ans+='9';
            }
            return ans;
        }
        if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            string ans="";
            for(int i=1;i<=n;i++){
                if(i==1||i==n)
                {
                    ans+='8';
                    continue;
                }
                int mid=n/2+n%2;
                if(i==mid){
                    if(n%2==0){
                        ans+='7';
                        ans+='7';
                        i++;
                    }
                    else{
                        ans+='8';
                    }
                }
                else ans+='9';
            }
            return ans;
        }
        if(k==8){
            if(n==1)return "8";
            if(n==2)return "88";
            string ans="";
            for(int i=1;i<=n;i++){
                if(i==1||i==n||i==2||i==n-1||i==3||i==n-2)ans+='8';
                else ans+='9';
            }
            return ans;
        }
        if(n==1)return "7";
        if(n==2)return "77";
        string res="";
        for(int i=1;i<=n;i++)res+='0';
        for(int num=0;num<=9;num++){
            string ans="";
        for(int i=1;i<=n;i++){
            int mid=n/2+n%2;
            if(i!=mid){
                ans+='9';
                continue;
            }
            if(n%2==0){
                ans+=(num+'0');
                ans+=(num+'0');
                i++;
            }
            else ans+=(num+'0');
        }
           int sum=0;
            for(int i=1;i<=n;i++){
                sum=sum*10+(ans[i-1]-'0');
                sum%=7;
            }
            if(sum==0)res=max(res,ans);
        }

        return res;
    }
};
