class Solution {
public:
    string SMulty(char ch, int num) {
        string ret;
        for (int i = 0; i < num; ++i) {
            ret += ch;
        }
        return ret;
    }

    string largestPalindrome2(int n) {
        if (n <= 2) {
            return SMulty('8', n);
        } else {
            return "8" + SMulty('9', n - 2) + "8";
        }
    }

    string largestPalindrome4(int n) {
        if (n <= 4) {
            return SMulty('8', n);
        } else {
            return "88" + SMulty('9', n - 4) + "88";
        }
    }

    string largestPalindrome5(int n) {
        if (n <= 2) {
            return SMulty('5', n);
        } else {
            return "5" + SMulty('9', n - 2) + "5";
        }
    }

    string largestPalindrome6(int n) {
        if (n == 1) {
            return "6";
        } else if (n == 2) {
            return "66";
        } else if (n == 3) {
            return "888";
        } else if (n == 4) {
            return "8778";
        } else {
            if (n % 2 == 0) {
                return "8" + SMulty('9', (n / 2) - 2) + "77" +
                       SMulty('9', (n / 2) - 2) + "8";
            } else {
                return "8" + SMulty('9', (n - 3) / 2) + "8" +
                       SMulty('9', (n - 3) / 2) + "8";
            }
        }
    }

    bool IsSeven(string a) {
        int b = 0;
        for (int i = 0; i < a.size(); i++) {
            b = 10 * b + (a[i] - '0');
            b = b % 7;
        }
        return b == 0;
    }

    string largestPalindrome7(int n) {
        if (n == 1) {
            return "7";
        } else if (n == 2) {
            return "77";
        }

        for (int i = 9; i >= 0; i--) {
            if (n % 2 == 0) {
                string ret = SMulty('9', (n / 2) - 1) + to_string(i) +
                             to_string(i) + SMulty('9', (n / 2) - 1);
                if (IsSeven(ret)) {
                    return ret;
                }
            } else {
                string ret = SMulty('9', (n - 1) / 2) + to_string(i) +
                             SMulty('9', (n - 1) / 2);
                if (IsSeven(ret)) {
                    return ret;
                }
            }
        }
        return "";
    }

    string largestPalindrome8(int n) {
        if (n <= 6) {
            return SMulty('8', n);
        }
        return "888" + SMulty('9', n - 6) + "888";
    }

    string largestPalindrome(int n, int k) {
        switch (k) {
        case 1:
        case 3:
        case 9:
            return SMulty('9', n);
        case 2:
            return largestPalindrome2(n);
        case 4:
            return largestPalindrome4(n);
        case 5:
            return largestPalindrome5(n);
        case 6:
            return largestPalindrome6(n);
        case 7:
            return largestPalindrome7(n);
        case 8:
            return largestPalindrome8(n);
        }
        return "";
    }
};
