class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            ans = '9' * n
        elif k == 2:
            if n <= 2:
                ans = '8' * n
            else:
                ans = '8' + '9' * (n - 2) + '8'
        elif k == 4:
            if n <= 4:
                ans = '8' * n
            else:
                ans = '88' + '9' * (n - 4) + '88'
        elif k == 5:
            if n <= 2:
                ans = '5' * n
            else:
                ans = '5' + '9' * (n - 2) + '5'
        elif k == 6:
            if n <= 2:
                ans = '6' * n
            else:
                mid = '8' if n & 1 else '77'
                m = (n - 3) // 2
                ans = '8' + '9' * m + mid + '9' * m + '8'
        elif k == 7:
            if n <= 2:
                ans = '7' * n
            else:
                s = ['9'] * n
                if n & 1:
                    s[n // 2] = '0'
                else:
                    s[n // 2] = s[n // 2 - 1] = '0'
                mod = 0
                for c in s:
                    mod = (mod * 10 + int(c)) % k
                p = 1
                for i in range(n - 1, n // 2, -1):
                    p = p * 10 % k
                if n & 1:
                    for j in range(9, -1, -1):
                        if (j * p + mod) % k == 0:
                            s[n // 2] = str(j)
                            break
                else:
                    for j in range(9, -1, -1):
                        if (j * 11 * p + mod) % k == 0:
                            s[n // 2] = s[n // 2 - 1] = str(j)
                            break
                ans = "".join(s)
        elif k == 8:
            if n <= 6:
                ans = '8' * n
            else:
                ans = '888' + '9' * (n - 6) + '888'

        return ans
