#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    // Function to check if a string representing a number is divisible by 7
    bool divisible(const string& num, int k) {
        int remainder = 0;
        for (char c : num) {
            remainder = (remainder * 10 + (c - '0')) % k;
        }
        return remainder == 0;
    }

    string largestPalindrome(int n, int k) {
        string s = "";
        if (k == 1 || k == 3 || k == 9) {
            s = string(n, '9');
        } else if (k == 2) {
            s = string(n, '9');
            s[0] = '8';
            s[n - 1] = '8';
        } else if (k == 4) {
            if(n==1)
            {
                return "8";
            }
            if(n==2)
            {
                return "88";
            }
            else if(n==3)
            {
                return "888";
            }

            s = string(n, '9');
            s[0] = '8';
            s[1]='8';
            s[n-2]='8';
            s[n - 1] = '8';

        }
        else if(k==8)
        {
            if(n==1)
            {
                return "8";
            }
            else if(n==2)
            {
                return "88";
            }
            else if(n==3)
            {
                return "888";

            }
            else if(n==4)
            {
                return "8888";
            }
            s=string(n, '9');
            s[0]='8';
            s[1]='8';
            s[2]='8';
            s[n-1]='8';
            s[n-2]='8';
            s[n-3]='8';
        } 
        else if (k == 5) {
            s += '5';
            for (int i = 1; i < n - 1; i++) {
                s += '9';
            }
            if (n > 1)
                s += '5';
        } else if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n == 3) {
                return "888";
            }
            string temp = "8";
            int m = n - (n % 2 ? 3 : 4);
            temp += string(m / 2, '9');
            temp += n % 2 ? "8" : "77";
            temp += string(m / 2, '9');
            temp += "8";
            return temp;
        } else if (k == 7) {
            if (n == 1) {
                return "7";
            }
            if (n == 2) {
                return "77";
            }
            string temp(n, '9');
            if (n % 2) {
                int mid = n / 2;
                for (char c = '9'; c >= '1'; c--) {
                    temp[mid] = c;
                    if (divisible(temp, 7)) {
                        return temp;
                    }
                }
            } else {
                int mid1 = n / 2;
                int mid2 = mid1 - 1;
                for (char c = '9'; c >= '1'; c--) {
                    temp[mid1] = c;
                    temp[mid2] = c;
                    if (divisible(temp, 7)) {
                        return temp;
                    }
                }
            }
        }
        return s;
    }
};
