class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9) return string(n,'9');
        if(n==1)
        {
            string a;
            for(int i=9;i>0;--i)
            {
                if(i%k==0)
                {
                    a=to_string(i);
                    break;
                }
            }
            return a;
        }
        if(n==2)
        {
            string a;
            for(int i=99;i>=11;i-=11)
            {
                if(i%k==0)
                {
                    a=to_string(i);
                    break;
                }
            }
            return a;
        }
        if(k==2) return string(1,'8')+string(n-2,'9')+string(1,'8');
        if(k==5) return string(1,'5')+string(n-2,'9')+string(1,'5');
        if(k==4)
        {
            if(n==3) return "888";
            return string("88")+string(n-4,'9')+string("88");
        }
        if(k==8)
        {
            if(n<=6) return string(n,'8');
            return string("888")+string(n-6,'9')+string("888");
        }
        if(k==6)
        {
            string a;
            if(n%2==1)
            {
                a=string("8")+string(n-2,'9')+string("8");
                int sum=8+8+(n-2)*9;
                a[n/2]-=sum%3;
            }
            else
            {
                a=string("8")+string(n-2,'9')+string("8");
                a[n/2]='7';
                a[n/2-1]='7';
            }
            return a;
        }
        string a(n,'9');
        vector<int> p(n);
        p[0]=1;
        for(int i=1;i<n;++i)
        {
            p[i]=p[i-1]*10%7;
        }
        int sum=accumulate(p.begin(),p.end(),0)*9%7;
        if(n%2==1)
        {
            for(;sum%7!=0;)
            {
                a[n/2]-=1;
                sum=(sum-p[n/2]+7);
            }
        }
        else
        {
            vector<int> m7(n+1);
            m7[0]=11%7;
            for(int i=1;i<=n;++i)
            {
                m7[i]=m7[i-1]*10%7;
            }
            for(;sum%7!=0;)
            {
                a[n/2]-=1;
                a[n/2-1]-=1;
                sum=(sum-m7[n/2-1]+7);
            }
        }
        return a;
    }
};
