class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9"*n
        elif k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + "9"*(n-2) + "8"
        elif k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9"*(n-4) + "88"
        elif k == 5:
            if n == 1:
                return "5"
            else:
                return "5" + "9"*(n-2) + "5"
        elif  k== 8:
            if n <= 6:
                return "8"*n
            else:
                return "888" + "9"*(n-6) + "888"
        elif k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            elif n % 2:
                return "8" + "9"*((n-3)//2) + "8" + "9"*((n-3)//2) + "8"
            else:
                return "8" + "9"*((n-4)//2) + "77" + "9"*((n-4)//2) + "8"
        else:
            curr = 0
            for i in range(n):
                curr = (curr*10 +9)% 7
            if n%2:
                v = pow(10,n//2 , 7)
            else:
                v = pow(10, n//2, 7) + pow(10, n//2-1, 7)
            i = 0
            while curr:
                curr = (curr - v)% 7
                i += 1
            if n%2:
                return  "9"*((n-1)//2) + str(9-i) + "9"*((n-1)//2)
            else:
                return  "9"*((n-2)//2) + str(9-i) + str(9-i) + "9"*((n-2)//2)
