#include <bits/stdc++.h>
using namespace std;

using ll = long long;


bool isDivisibleBy7(string s)
{
    int remainder = 0;
    for (int i = 0; i < s.length(); i++)
    {
        int digit = remainder * 10 + (s[i] - '0');
        remainder = digit % 7;
    }

    return remainder == 0;
}

class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k == 1) {
            return string(n, '9');
        }
        if(k == 2) {
            string ans = string(n, '9');
            ans[0] = ans[n - 1] = '8';
            return ans;
        }
        if(k == 3) {
            string ans = string(n, '9');
            return ans;
        }
        if(k == 4) {
            string ans = string(n, '8');
            if(n <= 3) {
                return string(n, '8');
            }
            for(int i = 2; i < n - 2; i++) ans[i] = '9';
            return ans;
        }
        if(k == 5) {
            string ans = string(n, '9');
            ans[0] = ans[n - 1] = '5';
            return ans;
        }
        if(k == 6) {
            if(n <= 2) {
                return string(n, '6');
            }
            string ans = string(n, '9');
            ans[0] = ans[n - 1] = '8';
            ll rmv = ((n * 9) - 2) % 3;
            if(rmv) {
                if(n & 1) {
                    ans[n / 2] -= rmv;
                }else {
                    if(rmv & 1) {
                        ans[n / 2] -= 2;
                        ans[(n - 1) / 2] -= 2;
                    }else {
                        ans[n / 2] -= 1;
                        ans[(n - 1) / 2] -= 1;
                    }
                }
            }
            return ans;
        }
        if(k == 7) {
            if(n == 1) return "7";
            if(n == 2) return "77";

            string ans = string(n, '9');
            if(n & 1) {
                for(char i = '9'; i >= '0'; i--) {
                    ans[n / 2] = i;
                    if(isDivisibleBy7(ans)) {
                        return ans;
                    }
                }
            }else {
                for(char i = '9'; i >= '0'; i--) {
                    ans[n / 2] = i;
                    ans[(n / 2) - 1] = i;
                    if(isDivisibleBy7(ans)) {
                        return ans;
                    }
                }
            }
        }
        if(k == 8) {
            string ans = string(n, '8');
            if(n <= 6) {
                return string(n, '8');
            }
            for(int i = 3; i < n - 3; i++) ans[i] = '9';
            return ans;
        }
        if(k == 9) {
            return string(n, '9');
        }
        return "";
    }
};
