class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans;
        int i;
        for (i = 0; i < n; i++) ans += '9';
        switch (k) {
            case 1:
            return ans;

            case 2:
            ans[0] = ans[n - 1] = '8';
            return ans;

            case 3:
            return ans;

            case 4:
            ans[0] = ans[n - 1] = '8';
            if (n <= 2) return ans;
            ans[1] = ans[n - 2] = '8';
            return ans;

            case 5:
            ans[0] = ans[n - 1] = '5';
            return ans;

            case 6:
            if (n == 1) return "6";
            if (n == 2) return "66";

            ans[0] = ans[n - 1] = '8';
            if (n % 2 == 1) ans[n / 2] = '8';
            else ans[n / 2 - 1] = ans[n / 2] = '7';
            return ans;

            case 7:
            if (n % 6 == 0) return ans;
            if (n % 6 == 1) {
                if ((n / 6) % 2 == 1) {
                    ans[n / 2] = '4';
                } else {
                    ans[n / 2] = '7';
                }
                return ans;
            }
            if (n % 6 == 2) {
                if ((n / 6) % 2 == 1) {
                    ans[n / 2] = ans[n / 2 - 1] = '4';
                } else {
                    ans[n / 2] = ans[n / 2 - 1] = '7';
                }
                return ans;
            }
            if (n % 6 == 3) {
                if ((n / 6) % 2 == 1) {
                    ans[n / 2] = '6';
                } else {
                    ans[n / 2] = '5';
                }
                return ans;
            }
            if (n % 6 == 4) {
                if ((n / 6) % 2 == 1) {
                    ans[n / 2] = ans[n / 2 - 1] = '4';
                } else {
                    ans[n / 2] = ans[n / 2 - 1] = '7';
                }
                return ans;
            }
            if (n % 6 == 5) {
                if ((n / 6) % 2 == 1) {
                    ans[n / 2] = '4';
                } else {
                    ans[n / 2] = '7';
                }
                return ans;
            }

            case 8:
            if (n <= 6) {
                ans = "";
                for (i = 0; i < n; i++) ans += '8';
                return ans;
            }
            ans[0] = ans[n - 1] = '8';
            ans[1] = ans[n - 2] = '8';
            ans[2] = ans[n - 3] = '8';
            return ans;

            case 9:
            return ans;
        }
        return "";
    }
};
