class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res;
        if(k == 1 || k == 3 || k == 9){
            for(int i = 0 ; i < n ; i ++){
                res += "9";
            }
            return res;
        }
        else if(k == 2 || k == 4 || k == 8){
            if(k == 2){
                for(int i = 0 ; i < n ; i ++){
                    if(i == 0 || i == n - 1){
                        res += "8";
                    }
                    else{
                        res += "9";
                    }
                }
            }
            if(k == 4){
                for(int i = 0 ; i < n ; i ++){
                    if(i == 0 || i == n - 1 || i == 1 || i == n - 2){
                        res += "8";
                    }
                    else{
                        res += "9";
                    }
                }
            }
            if(k == 8){
                for(int i = 0 ; i < n ; i ++){
                    if(i == 0 || i == n - 1 || i == 1 || i == n - 2 || i == 2 || i == n - 3){
                        res += "8";
                    }
                    else{
                        res += "9";
                    }
                }
            }
            return res;
        }
        else if(k == 5){
            for(int i = 0 ; i < n ; i ++){
                if(i == 0 || i == n - 1){
                    res += "5";
                }
                else{
                    res += "9";
                }
            }
            return res;
        }
        else if(k == 6){
            if(n == 1){
                return "6";
            }
            else if(n == 2){
                return "66";
            }
            else if(n % 2 == 0){
                for(int i = 0 ; i < n ; i ++){
                    if(i == 0 || i == n - 1){
                        res += "8";
                    }
                    else if((i == n / 2) || (i == n / 2 - 1)){
                        res += "7";
                    }
                    else{
                        res += "9";
                    }
                }
            }
            else{
                for(int i = 0 ; i < n ; i ++){
                    if(i == 0 || i == n - 1){
                        res += "8";
                    }
                    else if(i == (n-1) / 2){
                        res += "8";
                    }
                    else{
                        res += "9";
                    }
                }
            }
            return res;
        }
        else if(k == 7){
            if(n % 12 == 0 || n % 12 == 6){
                for(int i = 0 ; i < n ; i ++){
                    res += "9";
                }
            }
            else if(n%12 == 1 || n % 12 == 5){
                for(int i = 0 ; i < n ; i ++){
                    if(i != (n-1)/2){
                        res += "9";
                    }
                    else{
                        res += "7";
                    }
                }
            }
            else if(n%12 == 7 || n%12 == 11){
                for(int i = 0 ; i < n ; i ++){
                    if(i != (n-1)/2){
                        res += "9";
                    }
                    else{
                        res += "4";
                    }
                }
            }
            else if(n%12 == 2 || n%12 == 4){
                for(int i = 0 ; i < n ; i ++){
                    if((i != n/2) && (i != n/2 - 1)){
                        res += "9";
                    }
                    else{
                        res += "7";
                    }
                }
            }
            else if(n%12 == 8 || n%12 == 10){
                for(int i = 0 ; i < n ; i ++){
                    if((i != n/2) && (i != n/2 - 1)){
                        res += "9";
                    }
                    else{
                        res += "4";
                    }
                }
            }
            else if(n%12 == 3){
                for(int i = 0 ; i < n ; i ++){
                    if((i != (n-1)/2)){
                        res += "9";
                    }
                    else{
                        res += "5";
                    }
                }
            }
            else if(n%12 == 9){
                for(int i = 0 ; i < n ; i ++){
                    if((i != (n-1)/2)){
                        res += "9";
                    }
                    else{
                        res += "6";
                    }
                }
            }
        }
        return res;
    }
};
