    bool checkSevenDivisibility(string s){
        int rem = 0, n = s.length();
        for (int i = 0; i < n; i++) {
            int d = rem * 10 + (s[i] - '0');
            rem = d % 7;
        }
        return (rem == 0);
    }
    string largestPalindrome(int n, int k) {
        if(k == 1 or k == 3 or k == 9){
            return string(n, '9');
        }
        if(k == 2){
            return "8" + string(max(0,n-2),'9') + string(min(1,n-1),'8');

        }
        if(k == 5){
            return "5" + string(max(0,n-2),'9') + string(min(1,n-1),'5');

        }
        if(k == 4){
            if(n < 5){
                return string(n,'8');
            }
            return "88" + string(n-4,'9') + "88";
        }
        if(k == 8){
            if(n < 7){
                return string(n,'8');
            }
            return "888" + string(n-6,'9') + "888";
        }
        if(k == 6){
            if(n < 3){
                return string(n, '6');
            }
            string res = "8" + string(n-2, '9') + "8";
            if(n % 2){
                res[n / 2] = '8';
            } else {
                res[n / 2 - 1] = '7';
                res[n / 2] = '7';
            }
            return res;
        }
        if(k == 7){
            string part = string((n-1)/2,'9');
            for(char c = '9'; c >= '0'; c--){
                string res = part + string(2 - n % 2, c) + part;
                if(checkSevenDivisibility(res))
                    return res;
            }
        }
        return "";
    }
