class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1||k==3||k==9){
            return string(n,'9');
        }
        if(k==2){
            string res(n,'9');
            res[0]='8';
            res.back()='8';
            return res;
        }
        if(k==5){
            string res(n,'9');
            res[0]='5';
            res.back()='5';
            return res;
        }
        if(k==4){
            if(n==1){
                return "8";
            }
            string res(n,'9');
            res[0]='8';
            res[1]='8';
            res[n-1]='8';
            res[n-2]='8';
            return res;
        }
        if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            string res(n,'9');
            res[0]='8';
            res[n-1]='8';
            if(n&1){
                res[n/2]='8';
            }
            else{
                res[n/2]='7';
                res[n/2-1]='7';
            }
            return res;
        }
        if(k==7){
            string res(n,'9');
            int r=0;
            for(int i=0;i<n;++i){
                r=10*r+9;
                r%=7;
            }
            if(n&1){
                int t=1;
                for(int i=0;i<n/2;++i){
                    t*=10;
                    t%=7;
                }
                for(int i=0;i<7;++i){
                    if(t*i%7==r){
                        res[n/2]-=i;
                        break;
                    }
                }
            }
            else{
                int t=11;
                for(int i=0;i<n/2-1;++i){
                    t*=10;
                    t%=7;
                }
                for(int i=0;i<7;++i){
                    if(t*i%7==r){
                        res[n/2]-=i;
                        res[n/2-1]-=i;
                        break;
                    }
                }
            }
            return res;
        }
        if(n==1){
            return "8";
        }
        if(n==2){
            return "88";
        }
        string res(n,'9');
        res[0]='8';
        res[1]='8';
        res[2]='8';
        res[n-1]='8';
        res[n-2]='8';
        res[n-3]='8';
        return res;
    }
};
