class Solution {
public:
    bool check(string s) {
        ll base = s[0] - 'a';
        for (int i = 1; i < s.size(); i++) {
            base = base * 10 + s[i] - '0';
            base %= 7;
        }
        return base % 7 ? 0 : 1;
    }
    string largestPalindrome(int n, int k) {
        if (n == 1) {
            if (k == 1 || k == 3 || k == 9) return "9";
            if (k == 2 || k == 4 || k == 8) return "8";
            if (k == 7) return "7";
            if (k == 6) return "6";
            if (k == 5) return "5";
        }
        if (k == 1 || k == 9 || k == 3) {
            return string(n, '9');
        } else if (k == 2) {
            if (n <= 2) {
                return string(n, '8');
            }
            return "8" + string(n - 2, '9') + "8";
        } else if (k == 5) {
            if (n <= 2) {
                return string(n, '5');
            }
            return "5" + string(n - 2, '9') + "5";
        } else if (k == 6) {
            if (n == 2) return "66";
            if (n == 3) return "888";
            string base((n - 3) / 2, '9');
            if (n % 2) {
                return '8' + base + '8' + base + '8';
            } else {
                return '8' + base + "77" + base + '8';
            }
        } else if (k == 7) {
            if (n == 2) return "77";
            if (n == 3) return "959";
            if (n == 4) return "9779";
            if (n == 5) return "99799";
            if (n == 6) return "999999";
            if (n == 7) return "9994999";
            if (n == 8) return "99944999";
            string base(n / 2, '9');
            if (n % 2) {
                for (int i = 9; i >= 0; i--) {
                    string cur = base + char(i + '0') + base;
                    if (check(cur)) return cur;
                }
            } else {
                base.pop_back();
                for (int i = 99; i >= 11; i -= 11) {
                    string cur = base + to_string(i) + base;
                    if (check(cur)) return cur;
                }
            }
        } else if (k == 8) {
            if (n <= 6) {
                return string(n, '8');
            }
            return "888" + string(n - 6, '9') + "888";
        } else if (k == 4) {
            if (n <= 4) {
                return string(n, '8');
            }
            return "88" + string(n - 4, '9') + "88";
        }
        return " ";
    }
};
