class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1 or k==3 or k==9:
            return '9'*n
        if k==2:
            if n==1:
                return '8'
            return '8'+'9'*(n-2)+'8'
        if k==4:
            if n<=3:
                return '8'*n
            return '88'+'9'*(n-4)+'88'
        if k==5:
            if n==1:
                return '5'
            return '5'+'9'*(n-2)+'5'
        if k==6:
            if n<=2:
                return '6'*n
            if n%2==0:
                return '8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8'
            else:
                return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8'
        if k==8:
            if n<6:
                return '8'*n
            return '888'+'9'*(n-6)+'888'
        if k==7:
            if n<=2:
                return '7'*n
            sys.set_int_max_str_digits(100005)
            if n%2==0:
                num = 10**n-1-10**(n//2)*9-10**(n//2-1)*9
                for i in range(9,-1,-1):
                    if (num+(10**(n//2)+10**(n//2-1))*i)%7==0:
                        return str(num+(10**(n//2)+10**(n//2-1))*i)
            else:
                num = 10**n-1-10**(n//2)*9
                for i in range(9,-1,-1):
                    if (num+(10**(n//2))*i)%7==0:
                        return str(num+(10**(n//2))*i)
