class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100005)
        if k==1 or k==3 or k==9:
            return "9"*n
        elif k==2:
            if n==1:
                return "8"
            return "8"+"9"*(n-2)+"8"
        elif k==4:
            if n<=4:
                return "8"*n
            else:
                return "88"+"9"*(n-4)+"88"
        elif k==8:
            if n<=6:
                return "8"*n
            else:
                return "888"+"9"*(n-6)+"888"
        elif k==5:
            if n==1:
                return "5"
            return "5"+"9"*(n-2)+"5"
        elif k==6:
            if n==1:
                return "6"
            if n==2:
                return "66"
            if n%2==0:
                k=9
                while True:
                    s = str(k)
                    c = "8"+"9"*(n//2-2)+s+s+"9"*(n//2-2)+"8"
                    if int(c)%6==0:
                        return c
                    k-=1
            else:
                k=9
                while True:
                    s = str(k)
                    c = "8"+"9"*(n//2-1)+s+"9"*(n//2-1)+"8"
                    if int(c)%6==0:
                        return c
                    k-=1
        elif k==7:
            if n%2==0:
                k = 9
                while True:
                    s = str(k)
                    c = "9"*(n//2-1)+s+s+"9"*(n//2-1)
                    if int(c)%7==0:
                        return c
                    k-=1
            else:
                k=9
                while True:
                    s = str(k)
                    c = "9"*(n//2)+s+"9"*(n//2)
                    if int(c)%7==0:
                        return c
                    k-=1
