class Solution {
    public String largestPalindrome(int n, int k) {

        StringBuilder ans = new StringBuilder("");
        if(k == 1 || k == 3 || k == 9) for(int i = 0; i < n; i++) ans.append("9");
        else if(k == 2) {
            if(n == 1) ans.append("8");
            else if(n == 2) ans.append("88");
            else {
                ans.append("8");
                for(int i = 1; i < n - 1; i++) ans.append("9");
                ans.append("8");
            }
        } else if(k == 4) {
            if(n <= 4) for(int i = 0; i < n; i++) ans.append("8");
            else {
                ans.append("88");
                for(int i = 2; i < n - 2; i++) ans.append("9");
                ans.append("88");
            }
        } else if(k == 5) {
            if(n <= 2) for(int i = 0; i < n; i++) ans.append("5");
            else {
                ans.append("5");
                for(int i = 1; i < n - 1; i++) ans.append("9");
                ans.append("5");
            }
        } else if(k == 6) {
            if(n <= 2) for(int i = 0; i < n; i++) ans.append("6");
            else if(n == 3) ans.append("888");
            else if(n % 2 == 0) {
                ans.append("8");
                for(int i = 0; i < (n - 4) / 2; i++) ans.append("9");
                ans.append("77");
                for(int i = 0; i < (n - 4) / 2; i++) ans.append("9");
                ans.append("8");
            } else {
                ans.append("8");
                for(int i = 0; i < (n - 3) / 2; i++) ans.append("9");
                ans.append("8");
                for(int i = 0; i < (n - 3) / 2; i++) ans.append("9");
                ans.append("8");
            }
        } else if(k == 8) {
            if(n <= 6) for(int i = 0; i < n; i++) ans.append("8");
            else {
                ans.append("888");
                for(int i = 3; i < n - 3; i++) ans.append("9");
                ans.append("888");
            }
        } else {
            if(n <= 2) for(int i = 0; i < n; i++) ans.append("7");
            else {
                String Thala[] = new String[] {"5", "77", "7", "99", "4", "44", "6"};
                int i = 3;
                int j = 0;
                boolean f = true;
                while(i < n) {
                    i++;
                    if(j == 6) f = false;
                    else if(j == 0) f = true;
                    if(f) j++;
                    else j--;
                }

                for(i = 0; i < (n - Thala[j].length()) / 2; i++) ans.append("9");
                ans.append(Thala[j]);
                for(i = 0; i < (n - Thala[j].length()) / 2; i++) ans.append("9");
            }
        }

        return ans.toString();

    }
}
