class Solution {
public:
    int k;
    vector<int> res;
    static const int N = 1e5 + 1;
    int pd[N];
    int dp[N][10];
    int rec(int l,int n,int mod){

        int r = n-l+1;
        if(l > r) return mod == 0;
        if(dp[l][mod] != -1) return dp[l][mod];

        int pl = n-l,pr = l-1;
        if(l == r){
            for(int i=9;i>=0;--i){
                int curr = (i * pd[pl])%k;
                curr =( curr + mod)%k;

                if(rec(l+1,n,curr)){
                    res[l-1] = res[r-1] = i;
                    return dp[l][mod] = 1;
                } 
            }
            return dp[l][mod] = 0;
        }
        for(int i=9;i>=0;--i){
            int curr = (i * pd[pl] + i * pd[pr])%k;
            curr =( curr + mod)%k;

            if(rec(l+1,n,curr)){
                res[l-1] = res[r-1] = i;
                return dp[l][mod] = 1;
            } 
        }
        return dp[l][mod] = 0;
    }
    string largestPalindrome(int n, int k){
        this->k = k;
        res.resize(n);
        pd[0] = 1;
        for(int i=1;i<N;++i){
            pd[i] = (pd[i-1]*10)%k;
        }
        memset(dp,-1,sizeof(dp));
        rec(1,n,0);
        string s = "";
        for(auto i:res){
            s += i+'0';
        }
        return s;
    }
};
