class Solution {
public:
    string largestPalindrome(int n, int k) {
        string nines;
        for(int i = 0; i < n; i++) nines.push_back('9');

        if(k == 1 || k == 3 || k == 9) {
            return nines;
        }
        if(k == 5) {
            nines[0] = nines[n-1] = '5';
            return nines;
        }
        if(k == 2) {
            nines[0] = nines[n-1] = '8';
            return nines;
        }
        if(k == 4) {
            nines[0] = nines[n-1] = '8';
            if(n > 1) nines[1] = nines[n-2] = '8';
            return nines;
        }
        if(k == 8) {
            nines[0] = nines[n-1] = '8';
            if(n > 1) nines[1] = nines[n-2] = '8';
            if(n > 2) nines[2] = nines[n-3] = '8';
            return nines;
        }
        if(k == 6) {
            if(n == 1) return "6";
            if(n == 2) return "66";
            nines[0] = nines[n-1] = '8';
            if(n & 1) nines[n>>1] = '8';
            else nines[n/2-1] = nines[n/2] = '7';
            return nines;
        }
        // 7
        // 7
        // 77
        // 959
        // 9779
        // 99799
        // 999999
        // 9994999
        // 99944999
        // 999969999
        // 9999449999
        // 99999499999
        // 999999999999
        // 9999997999999
        // 99999977999999
        if(n == 1) return "7";
        if(n == 2) return "77";
        if(n & 1) {
            char s[] = "574647";
            char c = s[(n-3)/2%6];
            nines[n>>1] = c;
        } else {
            char s[] = "794497";
            char c = s[(n-4)/2%6];
            nines[n/2-1] = nines[n>>1] = c;
        }

        return nines;
    }
};
