class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return '9'*n
        if k == 2:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            return '8'+'9'*(n-2)+'8'
        if k == 4:
            if n <= 4:
                return '8'*n
            return '88'+'9'*(n-4)+'88'
        if k == 8:
            if n <= 6:
                return '8'*n
            return "888"+'9'*(n-6)+'888'
        if k == 5:
            if n == 1:
                return '5'
            if n == 2:
                return '55'
            return '5'+'9'*(n-2)+'5'
        if k == 3 or k == 9:
            return '9'*n
        if k == 6:
            if n <= 2:
                return '6'*n
            if n == 3:
                return '8'*3
            if n%2 == 1:
                n_9 = (n-3)//2
                return '8'+'9'*n_9+'8'+'9'*n_9+'8'
            n_9 = (n-4)//2
            return '8'+'9'*n_9+'77'+'9'*n_9+'8'
        if k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return '77'
            if n%2 == 1:
                n_9 = (n-1)//2
                for c in range(9, -1, -1):
                    s = '9'*n_9+str(c)+'9'*n_9
                    if self.is7(s):
                        return s
                n_9 = (n-3)//2
                for c in range(999, 99, -1):
                    if str(c) != str(c)[::-1]:
                        continue
                    s = '9'*n_9+str(c)+'9'*n_9
                    if self.is7(s):
                        return s
            if n%2 == 0:
                n_9 = (n-2)//2
                for c in range(99, 9, -1):
                    if str(c) != str(c)[::-1]:
                        continue
                    s = '9'*n_9+str(c)+'9'*n_9
                    if self.is7(s):
                        return s


    def is7(self, s):
        r = 0
        for c in s:
            r = (r*10 +int(c)) % 7
        return r == 0
