class Solution {
public:
  string largestPalindrome(int n, int k) {
    switch (k) {
    case 1:
    case 3:
    case 9:
      return string(n, '9');
    case 2:
      if (n < 3) {
        return string(n, '8');
      } else {
        return "8" + string(n - 2, '9') + "8";
      }
    case 4:
      if (n < 5) {
        return string(n, '8');
      } else {
        return "88" + string(n - 4, '9') + "88";
      }
    case 8:
      if (n < 7) {
        return string(n, '8');
      } else {
        return "888" + string(n - 6, '9') + "888";
      }
    case 5:
      if (n < 3) {
        return string(n, '5');
      } else {
        return "5" + string(n - 2, '9') + "5";
      }
    case 6:
      if (n == 1) {
        return "6";
      } else if (n == 2) {
        return "66";
      } else if (n == 3) {
        return "888";
      } else {
        string half = "8";
        while ((half.size() + 1) * 2 < n) {
          half += "9";
        };
        string rev = half;
        reverse(rev.begin(), rev.end());
        if (n - half.size() * 2 == 1) {
          return half + "8" + rev;
        } else { // == 2 {
          return half + "77" + rev;
        }

      }
    case 7:
      if (n == 1) {
        return "7";
      } else if (n == 2) {
        return "77";
      } else {
        int nine = (n - 1) / 2;
        for (char i = '9'; i >= '0'; --i) {
          string res =
            string(nine, '9') + string(n - nine * 2, i) + string(nine, '9');
          if (good7((res))) {
            return res;
          }
        }
      }
    }
    return "";
  }

  bool good7(string num) {
    int sum = 0, facto = 1;
    while (num.size()) {
      int a = num.size() ? num.back() - '0' : 0;
      if (num.size()) {
        num.pop_back();
      }
      int b = num.size() ? num.back() - '0' : 0;
      if (num.size()) {
        num.pop_back();
      }
      int c = num.size() ? num.back() - '0' : 0;
      if (num.size()) {
        num.pop_back();
      }
      sum += (c * 100 + b * 10 + a) * facto;
      facto = -facto;
    }
    return sum % 7 == 0;
  }
};
