class Solution {
private:
    long long dp[100005][10];
    string ans = "0";

    long long power(long long base, long long exp, long long mod) {
        base %= mod;
        long long result = 1;
        while (exp) {
            if (exp % 2) {
                result = result * base % mod;
                --exp;
            } else {
                base = base * base % mod;
                exp /= 2;
            }
        }
        return result;
    }

    bool solve(int i, int rem, long long k, long long len, string &s) {
        long long j = (len - 1) - i;
        if (j < i) {
            if (rem != 0) return false;
            if (s > ans) {
                ans = s;
            }
            return true;
        }

        if (dp[i][rem] != -1) return dp[i][rem];

        int start = (i == 0) ? 1 : 0;
        for (int d = 9; d >= start; --d) {
            long long x = power(10, i, k);
            long long y = power(10, j, k);
            x = (x * d) % k;
            y = (y * d) % k;

            s[i] = s[j] = char(d + '0');
            if (i != j) x = (x + y) % k;

            if (solve(i + 1, (rem + x) % k, k, len, s)) return dp[i][rem] = true;
        }

        return dp[i][rem] = false;
    }

public:
    string largestPalindrome(int n, int k) {
        memset(dp, -1, sizeof(dp));
        string s(n, '0');
        solve(0, 0, k, n, s);
        return ans;
    }
};
