class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in (1, 3, 9):
            return '9' * n

        if k == 2:
            if n <= 2:
                return '8' * n
            return f'8{(n - 2) * "9"}8'

        if k == 4:
            if n <= 4:
                return '8' * n
            return f'88{(n - 4) * "9"}88'

        if k == 8:
            if n <= 6:
                return '8' * n
            return f'888{(n - 6) * "9"}888'

        if k == 5:
            if n <= 2:
                return '5' * n
            return f'5{(n - 2) * "9"}5'

        if k == 6:
            if n <= 2:
                return '6' * n
            if n % 2:
                return f'8{((n-2)//2) * "9"}8{((n-2)//2) * "9"}8'
            else:
                return f'8{((n-4)//2) * "9"}77{((n-4)//2) * "9"}8'

        if n <= 2:
            return '7' * n
        """
        if n == 3:
            return '959'
        if n == 4:
            return '9779'
        if n == 5:
            return '99799'
        """
        if n % 6 == 0:
            return '9' * n


        for i in range(9, 0, -1):
            if n % 2:
                num = f'{(n//2)*"9"}{i}{(n//2)*"9"}'
            else:
                num = f'{((n-2)//2)*"9"}{i}{i}{((n-2)//2)*"9"}'

            is_even = 0
            curr_sum = 0
            for j in range(len(num) - 3, -1, -3):
                if is_even:
                    curr_sum += int(num[j: j + 3])
                else:
                    curr_sum -= int(num[j: j + 3])
                is_even ^= 1
            if j != 0:
                if is_even:
                    curr_sum += int(num[:j])
                else:
                    curr_sum -= int(num[:j])

            if curr_sum % 7 == 0:
                return num
