#define pb push_back
#define ff first
#define ss second
#define all(v) v.begin(),v.end()
#define allr(v) v.rbegin(),v.rend()
#define sz(v) (int)v.size()
#define deb(x) cout<<#x<<"="<<x<<endl;
#define pii pair<int,int>
#define vi vector<int>

class Solution {
public:

    bool check(string &s)
    {
        int rem=0,idx=0;
        while(idx<sz(s))
        {
            rem=rem*10+s[idx]-'0';
            if(rem>=7) rem%=7;
            idx++;
        }
        if(rem) return 0;
        return 1;
    }

    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1 or k==3 or k==9)
        {
            for(int i=0;i<n;i++) ans.push_back('9');
        }
        else if(k==2)
        {
            if(n==1) ans="8";
            else
            {
                ans.push_back('8');
                for(int i=1;i<n-1;i++) ans.push_back('9');
                ans.push_back('8');  
            } 
        }
        else if(k==4)
        {
            if(n==1) ans="8";
            else if(n==2) ans="88";
            else if(n==3) ans="888";
            else
            {
                ans.pb('8');
                ans.pb('8');
                for(int i=2;i<n-2;i++) ans.pb('9');
                ans.pb('8');
                ans.pb('8');
            }
        }
        else if(k==5)
        {
            if(n==1) ans="5";
            else
            {
                ans.pb('5');
                for(int i=1;i<n-1;i++) ans.pb('9');
                ans.pb('5');
            }
        }
        else if(k==6)
        {
            if(n==1) ans="6";
            else if(n==2) ans="66";
            else
            {
                ans.pb('8');
                for(int i=1;i<n-1;i++) ans.pb('9');
                ans.pb('8');
                if(n&1)
                {
                    ans[n/2]--;
                }
                else
                {
                    ans[n/2]-=2;
                    ans[n/2-1]-=2;
                }
            }
        }
        else if(k==7)
        {
            if(n==1) ans="7";
            else if(n==2) ans="77";
            else
            {
                for(int i=0;i<n;i++) ans.pb('9');
                while(check(ans)==0)
                {
                    if(n&1) ans[n/2]--;
                    else
                    {
                        ans[n/2-1]--;
                        ans[n/2]--;
                    }
                }
            }
        }
        else if(k==8)
        {
            if(n<=6)
            {
                for(int i=0;i<n;i++) ans.pb('8');
            }
            else
            {
                ans="888";
                for(int i=3;i<n-3;i++) ans.pb('9');
                ans+="888";
            }
        }
        return ans;
    }
};
