class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        if (k == 1 || k == 3 || k == 9){
            for (int i = 0; i < n; ++i){
                ans += '9';
            }
        }
        if (k == 2){
            ans += '8';
            for (int i = 1; i < n - 1; ++i){
                ans += '9';
            }
            if (n != 1){
                ans += '8';
            }
        }
        if (k == 4){
            if (n == 1){
                ans = "8";
            }
            else if (n == 2){
                ans = "88";
            }
            else if (n == 3){
                ans = "888";
            }
            else{
                ans = "88";
                for (int i = 2; i < n - 2; ++i){
                    ans += '9';
                }
                ans += "88";
            }
        }
        if (k == 5){
            ans = "5";
            for (int i = 1; i < n - 1; ++i){
                ans += '9';
            }
            if (n != 1){
                ans += '5';
            }
        }
        if (k == 6){
            if (n == 1){
                ans = "6";
            }
            else if (n == 2){
                ans = "66";
            }
            else{
                ans = '8';
                if (n % 2 == 1){
                    for (int i = 1; i < n / 2; ++i){
                        ans += '9';
                    }
                    ans += '8';
                    for (int i = 1; i < n / 2; ++i){
                        ans += '9';
                    }
                    ans += '8';
                }
                else{
                    for (int i = 1; i < n / 2 - 1; ++i){
                        ans += '9';
                    }
                    ans += "77";
                    for (int i = 1; i < n / 2 - 1; ++i){
                        ans += '9';
                    }
                    ans += '8';
                }

            }
        }
        if (k == 7){
            if (n == 1){
                ans = "7";
            }
            else if (n == 2){
                ans = "77";
            }
            else{
                ans = "9";
                int r = 2;
                int nr = 2;
                int cr = 1;
                if (n % 2 == 1){
                    for (int i = 1; i < n / 2; ++i){
                        nr = (nr * 10) % 7;
                        cr = (cr * 10) % 7;
                        r = (r + nr) % 7;
                        ans += '9';
                    }
                    nr = (nr * 10) % 7;
                    cr = (cr * 10) % 7;
                    for (int i = 0; i < n / 2; ++i){
                        nr = (nr * 10) % 7;
                        r = (r + nr) % 7;
                    }
                    char add_dig;
                    for (int i = 0; i <= 9; ++i){
                        if ((i * cr + r) % 7 == 0){
                            add_dig = '0' + i;
                        }
                    }
                    ans += add_dig;
                    for (int i = 0; i < n / 2; ++i){
                        ans += '9';
                    }
                }
                else{
                    for (int i = 1; i < n / 2 - 1; ++i){
                        nr = (nr * 10) % 7;
                        cr = (cr * 10) % 7;
                        r = (r + nr) % 7;
                        ans += '9';
                    }
                    cr = (cr * 10) % 7;
                    nr = (nr * 10) % 7;
                    nr = (nr * 10) % 7;
                    for (int i = 0; i < n / 2 - 1; ++i){
                        nr = (nr * 10) % 7;
                        r = (r + nr) % 7;
                    }
                    char add_dig;
                    for (int i = 0; i <= 9; ++i){
                        if ((r + cr * i * 11) % 7 == 0){
                            add_dig = '0' + i;
                        }
                    }
                    ans += add_dig;
                    ans += add_dig;
                    for (int i = 0; i < n / 2 - 1; ++i){
                        ans += '9';
                    }
                }
            }
        }
        if (k == 8){
            if (n <= 6){
                for (int i = 0; i < n; ++i){
                    ans += '8';
                }
            }
            else{
                ans += "888";
                for (int i = 3; i < n - 3; ++i){
                    ans += '9';
                }
                ans += "888";
            }
        }
        return ans;
    }
};
