class Solution {
public:
        int div(string num)
    {
        int n = num.length(), gSum=0;
        if (n == 0)
            return 1;

        if (n % 3 == 1) {
            num="00" + num;
            n += 2;
        }
        else if (n % 3 == 2) {
            num= "0" + num;
            n++;
        }

        int i, GSum = 0, p = 1;
        for (i = n - 1; i >= 0; i--) {

            int group = 0;
            group += num[i--] - '0';
            group += (num[i--] - '0') * 10;
            group += (num[i] - '0') * 100;

            gSum = gSum + group * p;

            p *= (-1);
        }
        return (gSum % 7 == 0);
    }

    string check_cases(int n, int k)
    {
        string result;
        if (k == 1 || k == 3 || k == 9)
        {
            int i = 0;
            while (i < n)
            {
                result += "9";
                i++;
            }
            return result;
        }
        else if (k == 2)
        {
            if (n == 1)
            {
                return "8";
            }
            else
            {
                result += "8";
                int i = 1;
                while (i < n - 1)
                {
                    result += "9";
                    i++;
                }
                result += "8";
                return result;
            }
        }
        else if (k == 4)
        {
            if (n == 1)
            {
                return "8";
            }
            else if (n == 2)
            {
                return "88";
            }
            else if (n == 3)
            {
                return "888";
            }
            else
            {
                result += "88";
                int i = 2;
                while (i < n - 2)
                {
                    result += "9";
                    i++;
                }
                result += "88";
                return result;
            }
        }
        else if (k == 5)
        {
            if (n == 1)
            {
                return "5";
            }
            else
            {
                result += "5";
                int i = 1;
                while (i < n - 1)
                {
                    result += "9";
                    i++;
                }
                result += "5";
                return result;
            }
        }
        else if (k == 6)
        {
            if (n == 1)
            {
                return "6";
            }    
            else if (n == 2)
            {
                return "66";
            }
            else
            {
                if (n % 2 == 0)
                {
                    result += "8";
                    int i = 1;
                    while (i < n - 1)
                    {
                        if (i == n / 2 || i == (n / 2) - 1)
                        {
                            result += "7";
                        }
                        else
                        {
                            result += "9";
                        }
                        i++;
                    }
                    result += "8";
                    return result;
                }
                else
                {
                    result += "8";
                    int i = 1;
                    while (i < n - 1)
                    {
                        if (i == n / 2)
                        {
                            result += "8";
                        }
                        else
                        {
                            result += "9";
                        }
                        i++;
                    }
                    result += "8";
                    return result;
                }
            }
        }
        else if (k == 7)
        {
            if (n == 1)
            {
                return "7";
            }
            else if (n == 2)
            {
                return "77";
            }
            // else if(n == 3)
            // {
            //     return "959";
            // }
            else
            {
                int i = 0;
                while (i < n)
                {
                    result += "9";
                    i++;
                }
                if (n % 2 == 0)
                {
                    int j = 0;
                    while (j <= 9)
                    {
                        if (n / 2 < result.size() && (n / 2 - 1) < result.size())
                        {
                            result[n / 2] = '9' - j;
                            result[(n / 2) - 1] = '9' - j;
                        }
                        if (div(result))
                        {
                            return result;
                        }
                        j++;
                    }    
                }
                else
                {
                    int j = 0;
                    while (j <= 9)
                    {
                        if (n / 2 < result.size())
                        {
                            result[n / 2] = '9' - j;
                        }
                        if (div(result))
                        {
                            return result;
                        }
                        j++;
                    }
                }
                return result;
            }   
        }
        else
        {
            if (n == 1)
            {
                return "8";
            }
            else if (n == 2)
            {
                return "88";
            }
            else if (n == 3)
            {
                return "888";
            }
            else if (n == 4)
            {
                return "8888";
            }
            else if (n == 5)
            {
                return "88888";
            }
            else
            {
                result += "888";
                int i = 3;
                while (i < n - 3)
                {
                    result += "9";
                    i++;
                }
                result += "888";
                return result;
            } 
        }
    }

    string largestPalindrome(int n, int k) {
        return check_cases(n, k);
    }
};
