class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s;
        if(k==2){

            for(int i=0;i<n-1;i++){
                s+='9';
            }
            s[0]='8';
            s+='8';

        }
        else if(k==3 || k==9 || k==1){
            for(int i=0;i<n;i++){
                s+='9';
            }
        }
        else if(k==4 || k==8){
            for(int i=0;i<n;i++){
                s+='8';
            }
            if(k==8){
                for(int i=3;i<n-3;i++){
                    s[i]='9';
                }
            }
            if(k==4){
                for(int i=2;i<n-2;i++){
                    s[i]='9';
                }
            }
        }
        else if(k==5){
            for(int i=0;i<n-1;i++){
                s+='9';
            }
            s[0]='5';
            s+='5';
        }
        else if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            for(int i=0;i<n-1;i++){
                s+='9';
            }
            s[0]='8';
            s+='8';

            // if(n%2==0){
            int sum=0;
                for(int i=0;i<n;i++){
                    if(i==n/2 || i==(n-1)/2){

                    }
                    else{
                        sum+=s[i]-'0';
                    }
                }
            // }
            if(n%2==0){
                for(char x='1';x<='9';x++){
                    int val= 2*(x-'0');
                    if((val+sum)%3==0){
                        s[(n)/2]=x;
                        s[(n-1)/2]=x;
                    }
                }

            }
            else{
                for(char x='1';x<='9';x++){
                    int val= (x-'0');
                    if((val+sum)%3==0){
                        s[(n)/2]=x;
                    }
                }
            }
        }
        else if(k==7){
            if(n==1){
                return "7";
            }
            else if(n==2){
                return "77";
            }
            for(int i=0;i<n;i++){
                s+='9';
            }
            string ans=s;
            for(char x='1';x<='9';x++){
                if(n%2==0){
                    s[(n-1)/2]=x;
                    s[n/2]=x;

                    vector<int> v={1,3,2,6,4,5};
                    int diff=0;
                    int j=0;
                    for(int i=n-1;i>=0;i--){
                         int d = (s[i]-'0');
                        int val=d*v[j];
                        j++;
                        j%=6;
                        val%=7;
                        diff+=val;
                        diff%=7;
                    }
                    if(diff==0){
                        ans=s;
                    }
                    s=ans;
                }
                else{
                    // s[(n-1)/2]=x;
                    s[n/2]=x;

                    vector<int> v={1,3,2,6,4,5};
                    int diff=0;
                    int j=0;
                    for(int i=n-1;i>=0;i--){
                        int val= (s[i]-'0')*v[j];
                        j++;
                        j%=6;
                        val%=7;
                        diff+=val;
                        diff%=7;
                    }
                    if(diff==0){
                        ans=s;
                    }
                    s=ans;
                }
            }


        }
        return s;


    }
};
