class Solution:
    def get_remainder(self, num: str, k: int) -> int:
        remainder = 0
        for digit in num:
            remainder = (remainder*10 + int(digit)) % k
        return remainder

    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1 or k==3 or k==9:
            return "9"*n
        elif k==2:
            return "8"*n if n<=2 else "8"+("9"*(n-2))+"8"
        elif k==4:
            return "88"+("9"*(n-4))+"88" if n>=4 else "8"*n 
        elif k==5:
            return "5"*n if n<=2 else "5"+("9"*(n-2))+"5"
        elif k==6:
            if n==1:
                return "6"
            elif n==2:
                return "66"
            else:
                if n%2 == 1:
                    return "8"+("9"*((n-3)//2))+"8"+("9"*((n-3)//2))+"8"
                else:
                    return "8"+("9"*((n-4)//2))+"77"+("9"*((n-4)//2))+"8"
        elif k==7:
            if n<=2:
                return "7"*n
            else:
                if n%2==0:
                    for i in range(9,0,-1):
                        res = "9"*((n-2)//2) + (str(i)*2) + "9"*((n-2)//2)
                        if self.get_remainder(res, k) == 0:
                            return res
                    return ""
                else:
                    for i in range(9,0,-1):
                        res = "9"*(n//2) + str(i) + "9"*(n//2)
                        if self.get_remainder(res, k) == 0:
                            return res
                    return ""
        elif k==8:
            return "888"+("9"*(n-6))+"888" if n>=6 else "8"*n
        return ""
