#include <vector>
#include <string>
#include <algorithm>

using namespace std;

class Solution {
public:
    char A[100001][7];
    char B[100001][7];
    int C[100001][7];
    int D[100001][7];

    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }

        if (k == 2) {
            if (n == 1) {
                return "8";
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            return result;
        }

        if (k == 5) {
            if (n == 1) {
                return "5";
            }
            string result(n, '9');
            result[0] = '5';
            result[n - 1] = '5';
            return result;
        }

        if (k == 4) {
            if (n <= 3) {
                return string(n, '8');
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            result[1] = '8';
            result[n - 2] = '8';
            return result;
        }

        if (k == 8) {
            if (n <= 5) {
                return string(n, '8');
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            result[1] = '8';
            result[n - 2] = '8';
            result[2] = '8';
            result[n - 3] = '8';
            return result;
        }

        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n == 3) {
                return "888";
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            if (n % 2 == 1) {
                result[n / 2] = '8';
            } else {
                result[n / 2] = '7';
                result[(n / 2) - 1] = '7';
            }
            return result;
        }

        if (k == 7) {
            if (n == 1) {
                return "7";
            }

            fill(&A[0][0], &A[0][0] + sizeof(A), 0);
            fill(&B[0][0], &B[0][0] + sizeof(B), 0);

            for (int i = 1; i < 10; ++i) {
                A[1][i % 7] = '0' + i;
                if (i != 0) {
                    B[1][i % 7] = '0' + i;
                }
            }

            int E = 10 % 7;
            for (int i = 0; i <= 100000 - 2; ++i) {
                for (int j = 0; j < 7; ++j) {
                    if ((i == 0 && j != 0) || (i > 0 && A[i][j] == 0)) {
                        continue;
                    }
                    for (int digit = 9; digit >= 0; --digit) {
                        int F = (E * digit + digit + j * 10) % 7;
                        if (A[i + 2][F] == 0 || A[i + 2][F] < '0' + digit) {
                            A[i + 2][F] = '0' + digit;
                            C[i + 2][F] = j;
                        }
                        if (digit == 0) continue;
                        if (B[i + 2][F] == 0 || B[i + 2][F] < '0' + digit) {
                            B[i + 2][F] = '0' + digit;
                            D[i + 2][F] = j;
                        }
                    }
                }
                E = (E * 10) % 7;
            }

            int q = n;
            string front(1, B[q][0]);
            string back(1, B[q][0]);
            int mod = D[q][0];
            q -= 2;
            while (q) {
                front += A[q][mod];
                if (q > 1) back += A[q][mod];
                else break;
                mod = C[q][mod];
                q -= 2;
            }
            reverse(back.begin(), back.end());
            return front + back;
        }

        return "";
    }
};
