class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans="";
        bool vis[n+2][k];
        int sum[n+2];
        memset(vis,0,sizeof(vis));
        memset(sum,0,sizeof(sum));
        for(int i=n,s=1;i>=1;--i)
        {
            int pos=i;
            if(i+i>n)   pos=n+1-i;
            sum[pos]=(sum[pos]+s)%k;
            s=s*10%k;
        }
        int ed=(n+1)/2+1;
        vis[ed][0]=true;
        for(int i=ed-1;i>=1;--i)
            for(int a=0;a<k;++a)
                for(int b=0;b<10;++b)
                    vis[i][(a+b*sum[i])%k]|=vis[i+1][a];
        for(int i=1,now=0;i<ed;++i)
        {
            for(int a=10-1;a>=0;--a)
            {
                bool flag=false;
                for(int b=0;b<k;++b)
                    if((now+a*sum[i]+b)%k==0&&vis[i+1][b])
                    {
                        now=(now+a*sum[i])%k;
                        ans+=a+'0';
                        flag=true;
                        break;
                    }
                if(flag)    break;

            }
        }
        string tmp=ans;
        if(n&1) tmp.pop_back();
        reverse(tmp.begin(),tmp.end());
        ans+=tmp;
        return ans;
    }
};
