class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1 or k==3 or k==9:
            return '9'*n
        if k==2:
            if n==1:
                return '8'
            return '8'+'9'*(n-2)+'8'
        if k==4:
            if n==1:
                return '8'
            if n==2:
                return '88'
            if n==3:
                return '888'
            return '88'+'9'*(n-4)+'88'
        if k==5:
            if n==1:
                return '5'
            return '5'+'9'*(n-2)+'5'
        if k==6:
            if n==1:
                return '6'
            if n==2:
                return '66'
            if n%2:
                return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2) +'8'   
            val=n//2
            s='8'+'9'*(val-2)+'7'
            return s+s[::-1]
        if k==7:   
            if n==1:
                return '7'
            if n==2:
                return '77'
            a=['5','77','7','99','4','44','6']
            i=0
            num=n
            n-=3
            flg=True
            while n:
                if flg:
                    i+=1
                else:
                    i-=1
                if i==6:
                    flg=False
                if i==0:
                    flg=True    
                n-=1
            s=a[i]
            val=len(s)

            return '9'*((num-val)//2)+ a[i] + '9'*((num-val)//2)

        if k==8:
            if n<=6:
                return '8'*n
            return '888'+'9'*(n-6)+'888'
