class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in (1, 3, 9):
            return "9"*n
        elif k == 2:
            if n == 1:
                return "8"
            return "8" + "9"*(n-2) + "8"
        elif k == 4:
            if n < 4:
                return "8"*n
            return "88" + "9"*(n-4) + "88"
        elif k == 8:
            if n < 6:
                return "8"*n
            return "888" + "9"*(n-6) + "888"
        elif k == 5:
            if n == 1:
                return "5"
            return "5" + "9"*(n-2) + "5"
        elif k == 6:
            if n < 3:
                return "6"*n
            elif n % 2:
                return "8" + (n-3)//2*"9" + "8" + (n-3)//2*"9" + "8"
            else:
                return "8" + (n-4)//2*"9" + "77" + (n-4)//2*"9" + "8"
        else:
            temp = ["7", "77", "5", "77", "7", "99", "4", "44", "6", "44", "4", "99"]
            t = temp[(n-1)%len(temp)]
            half_n = (n-len(t))//2
            return half_n*"9" + t + half_n*"9"
