class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        import sys
        sys.set_int_max_str_digits(0)

        if k == 1 or k == 9 or k == 3:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n-2) + '8'
        elif k == 4:
            if n == 1:
                return '8'
            elif n == 2:
                return '88'
            elif n == 3:
                return '888'
            else:
                return '88' + (n-4) * '9' + '88'
        elif k == 5:
            if n == 1:
                return '5'
            return '5'+ '9' * (n-2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            elif n == 2:
                return '66'
            # last/first digit is 8
            if n % 2 == 0:
                return '8' + '9' * (n//2-2) + '77' + '9' * (n//2-2) + '8'
            else:
                return '8' + '9' * (n//2-1) + '8' + '9' * (n//2-1) + '8'
        elif k == 8:
            if n == 1:
                return '8'
            elif n == 2:
                return '88'
            elif n == 3:
                return '888'
            elif n == 4:
                return '8888'
            elif n == 5:
                return '88888'
            elif n >= 6:
                return '888' + (n-6) * '9' + '888'
        elif k == 7:
            if n == 1:
                return '7'
            elif n == 2:
                return '77'
            # first/last digit is 9
            # 9xxxxx - 18 divides 7, so is 9xxxxx + 3
            if n % 2 == 1:
                for i in range(9, 0, -1):
                    if int('9' * (n//2) + str(i) + '9' * (n//2)) % 7 ==0:
                        return '9' * (n//2) + str(i) + '9' * (n//2)
            else:
                for i in range(9, 0, -1):
                    if int('9' * (n//2-1) + str(i) + str(i) + '9' * (n//2-1)) % 7 == 0:
                        return '9' * (n//2-1) + str(i) + str(i) +'9' * (n//2-1)
