int solve(string s){
    int c=0;
    int n=s.size();
    int p=1;
    for(int i=n-1;i>=0;i--)
    {
        c+=(((int)(s[i]-'0'))*p);
        p*=10;
        p%=7;
        c%=7;
    }
    if(c==0) return 1;
    return 0;
}
class Solution {
public:
    string largestPalindrome(int n, int k) {

        if(n==1)
        {
            string ans;
                ans.push_back('0');
            if(k==1) ans[0]= '9';
            if(k==2) ans[0]= '8';
            if(k==3) ans[0]= '9';
            if(k==4) ans[0]= '8';
            if(k==5) ans[0]= '5';
            if(k==6) ans[0]= '6';
            if(k==7) ans[0]= '7';
            if(k==8) ans[0]= '8';
            if(k==9) ans[0]= '9';
            return ans;
        }
        if(k==1 || k==3 || k==9)
        {
            string ans;
            for(int i=0;i<n;i++) ans.push_back('9');
            return ans;
        }
        if(k==2)
        {
            string ans;
            for(int i=0;i<n;i++) ans.push_back('9');
            ans[0]='8';
            ans[n-1]='8';
            return ans;
        }
        if(k==5)
        {
            string ans;
            for(int i=0;i<n;i++) ans.push_back('9');
            ans[0]='5';
            ans[n-1]='5';
            return ans;
        }
        if(k==4)
        {
            string ans;
            for(int i=0;i<n;i++) ans.push_back('9');
            ans[0]='8';
            ans[n-1]='8';
            if(n>=3)
            {
                ans[n-2]='8';
            }
            if(n>=4)
            {
                ans[1]='8';
            }
            return ans;
        }
        if(k==8)
        {
            string ans;
            for(int i=0;i<n;i++) ans.push_back('9');
            ans[0]='8';
            ans[n-1]='8';
            if(n>=3)
            {
                ans[n-2]='8';
            }
            if(n>=4)
            {
                ans[1]='8';
            }
            if(n>=5)
            {
                ans[n-3]='8';
            }
            if(n>=6)
            {
                ans[2]='8';
            }
            return ans;
        }
        if(k==6)
        {
            if(n==2) 
            {
                string ans;
                for(int i=0;i<n;i++) ans.push_back('6');
                return ans;
            }
            if(n%2==1)
            {
                string ans;
                for(int i=0;i<n;i++) ans.push_back('9');
                ans[0]='8';
                if(n-1>=0) ans[n-1]='8';
                ans[(n/2)]='8';
                return ans;
            }
            else
            {
                string ans;
                for(int i=0;i<n;i++) ans.push_back('9');
                ans[0]='8';
                if(n-1>=0) ans[n-1]='8';
                ans[n/2 - 1]='7';
                ans[n/2 ]='7';
                return ans;
            }
        }

        if(k==7)
        {
            if(n%2==1)
            {
                string ans;
                for(int i=0;i<n;i++) ans.push_back('9');
                for(int i=9;i>=0;i--)
                {
                    ans[n/2]=(char)('0'+i);
                    if(solve(ans)) return ans;
                }
                assert(false);
            }
            else
            {
                string ans;
                for(int i=0;i<n;i++) ans.push_back('9');
                for(int i=9;i>=0;i--)
                {
                    ans[n/2 - 1]=(char)('0'+i);
                    ans[n/2]=(char)('0'+i);
                    if(solve(ans)) return ans;
                }
                assert(false);
            }
        }
        string ans;
        return ans;


    }
};
