class Solution {
    public String largestPalindrome(int n, int k) {
        StringBuilder s = new StringBuilder();
        for (int i = 0; i < n; i++) s.append('9');

        if (k == 1) {
            return s.toString();
        } else if (k == 2) {
            s.setCharAt(0, '8');
            s.setCharAt(n - 1, '8');
            return s.toString();
        } else if (k == 3) {
            return s.toString();
        } else if (k == 4) {
            s.setCharAt(0, '8');
            if (n > 1) s.setCharAt(1, '8');
            s.setCharAt(n - 1, '8');
            if (n > 1) s.setCharAt(n - 2, '8');
            return s.toString();
        } else if (k == 5) {
            s.setCharAt(0, '5');
            s.setCharAt(n - 1, '5');
            return s.toString();
        } else if (k == 6) {
            if (n < 6) {
                if (n == 1) s.setCharAt(0, '6');
                else if (n == 2) {
                    s.setCharAt(0, '6');
                    s.setCharAt(1, '6');
                } else if (n == 3) {
                    s.setCharAt(0, '8');
                    s.setCharAt(1, '8');
                    s.setCharAt(2, '8');
                } else if (n == 4) {
                    s.setCharAt(0, '8');
                    s.setCharAt(1, '7');
                    s.setCharAt(2, '7');
                    s.setCharAt(3, '8');
                } else {
                    s.setCharAt(0, '8');
                    s.setCharAt(2, '8');
                    s.setCharAt(4, '8');
                }
            } else {
                if (n % 2 == 0) {
                    s.setCharAt(0, '8');
                    s.setCharAt(1, '9');
                    s.setCharAt(n / 2, '7');
                    s.setCharAt(n / 2 - 1, '7');
                    s.setCharAt(n - 1, '8');
                    s.setCharAt(n - 2, '9');
                } else {
                    s.setCharAt(0, '8');
                    s.setCharAt(n - 1, '8');
                    s.setCharAt(n / 2, '8');
                }
            }
            return s.toString();
        } else if (k == 7) {
            if (n == 1) s.setCharAt(0, '7');
            else if (n == 2) {
                s.setCharAt(0, '7');
                s.setCharAt(1, '7');
            } else {
                if (n % 2 == 0) {
                    for (int j = 9; j >= 1; j--) {
                        s.setCharAt(n / 2, (char) ('0' + j));
                        s.setCharAt((n - 1) / 2, (char) ('0' + j));
                        int rem = 0;
                        for (int i = 0; i < n; i++) {
                            int curr = s.charAt(i) - '0';
                            curr += rem * 10;
                            rem = curr % 7;
                        }
                        if (rem == 0) return s.toString();
                    }
                } else {
                    for (int j = 9; j >= 1; j--) {
                        s.setCharAt(n / 2, (char) ('0' + j));
                        int rem = 0;
                        for (int i = 0; i < n; i++) {
                            int curr = s.charAt(i) - '0';
                            curr += rem * 10;
                            rem = curr % 7;
                        }
                        if (rem == 0) return s.toString();
                    }
                }
            }
            return s.toString();
        } else if (k == 8) {
            s.setCharAt(0, '8');
            s.setCharAt(n - 1, '8');
            if (n > 1) {
                s.setCharAt(1, '8');
                s.setCharAt(n - 2, '8');
            }
            if (n > 2) {
                s.setCharAt(2, '8');
                s.setCharAt(n - 3, '8');
            }
            return s.toString();
        }
        return s.toString();
    }
}
