class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1||k==3||k==9) return string(n, '9');
        if(k==2){
            if(n==1) return "8";
            else return "8"+string(n-2, '9')+"8";
        }
        if(k==5){
            if(n==1) return "5";
            else return "5"+string(n-2, '9')+"5";
        }
        if(k==4){
            if(n<=4) return string(n, '8');
            else return "88"+string(n-4, '9')+"88";
        }
        if(k==8){
            if(n<=6) return string(n, '8');
            else return "888"+string(n-6, '9')+"888";
        }
        if(k==6){
            if(n&1){
                if(n==1) return "6";
                else return "8"+string(n/2-1, '9')+"8"+string(n/2-1, '9')+"8";
            }else{
                if(n==2) return "66";
                else return "8"+string(n/2-2, '9')+"77"+string(n/2-2, '9')+"8";
            }
        }
        if(n<=2) string(n, '7');
        int d[6]={1,3,2,6,4,5},res=0;
        string now=string(n, '9');
        for(int i=0;i<n;++i){
            res+=9*d[i%6];
            res%=7;
        }
        if(res==0) return now;
        if(n&1){
            while(res){
                now[n/2]--;
                res+=7-d[n/2%6];
                res%=7;
            }
        }else{
            while(res){
                now[n/2]--;
                now[n/2-1]--;
                res+=7-d[n/2%6];
                res+=7-d[(n/2-1)%6];
                res%=7;
            }
        }
        return now;
    }
};
