class Solution {
public:
    string toStr(int x) {
        string s;
        while (x) s += '0' + x % 10, x /= 10;
        reverse(begin(s), end(s));
        return s;
    }

    bool palin(int x) {
        string s = toStr(x);
        string t = s;
        reverse(begin(t), end(t));
        return t == s;
    }

    bool div7(string s) {
        int r = 0;
        for (char c: s) {
            r = (r * 10 + (c - '0')) % 7;
        }
        return r == 0;
    }

    string largestPalindrome(int n, int k) {
        if (n < 6) {
            int x = 1;
            for (int j = 1; j <= n; j++) x *= 10;
            --x;
            while (x % k != 0 || !palin(x)) --x;
            return toStr(x);
        }
        if (k == 5) {
            string s = '5' + string(n - 2, '9') + '5';
            return s;
        }
        if (k == 2) {
            string s = '8' + string(n - 2, '9') + '8';
            return s;
        }
        if (k == 4) {
            string s = "88" + string(n - 4, '9') + "88";
            return s;
        }
        if (k == 8) {
            string s = "888" + string(n - 6, '9') + "888";
            return s;
        }
        if (k == 1 || k == 3 || k == 9) {
            string s = string(n, '9');
            return s;
        }
        if (k == 6) {
            string s;
            if (n % 2 == 0) s = '8' + string((n - 4) / 2, '9') + "77" + string((n - 4) / 2, '9') + '8';
            else s = '8' + string((n - 3) / 2, '9') + "8" + string((n - 3) / 2, '9') + '8';
            return s;
        }
        string s;
        for (int x = 9; x >= 0; x--) {
            if (n % 2 == 0) {
                s = string((n - 2) / 2, '9') + string(2, '0' + x) + string((n - 2) / 2, '9');
            }
            else s = string((n - 1) / 2, '9') + string(1, '0' + x) + string((n - 1) / 2, '9');
            if (div7(s)) break;
        }
        return s;
    }
};
