class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """
        if n<=11:
            if n == 1:
                for i in range(9, -1, -1):
                    if i % k == 0:
                        return str(i)
            else:
                upper_limit = 10 ** (n // 2) - 1
                lower_limit = 10 ** (n // 2 - 1)

            for i in range(upper_limit, lower_limit - 1, -1):
                if n % 2 == 0:
                    pal = int(str(i) + str(i)[::-1])
                    if pal % k == 0:
                        return str(pal)
                else:
                    for j in range(9, -1, -1):
                        pal = int(str(i) + str(j) + str(i)[::-1])
                        if pal % k == 0:
                            return str(pal)

            return ""




        if k==1:
            return '9'*n
        if k==2:
            return '8'+'9'*(n-2)+'8'
        if k==3:
            return '9'*n
        if k==4:
            return '88'+'9'*(n-4)+'88'
        if k==5:
            return '5'+'9'*(n-2)+'5'
        if k==6:
            if n%2==0:
                return '8'+(n-4)//2*'9'+'77'+(n-4)//2*'9'+'8'
            else:
                return '8'+(n-3)//2*'9'+'8'+(n-3)//2*'9'+'8'
        if k==7:
            if n % 6 == 0:
                return '9'*n
            if n % 12 == 1:
                return '9'*(n//2)+'7'+'9'*(n//2)
            if n % 12 == 2:
                return '9' * (n // 2-1) + '77' + '9' * (n // 2-1)
            if n % 12 == 3:
                return '9'*(n//2)+'5'+'9'*(n//2)
            if n % 12 == 4:
                return '9' * (n // 2-1) + '77' + '9' * (n // 2-1)
            if n % 12 == 5:
                return '9'*(n//2)+'7'+'9'*(n//2)
            if n % 12 == 7:
                return '9'*(n//2)+'4'+'9'*(n//2)
            if n % 12 == 8:
                return '9' * (n // 2-1) + '44' + '9' * (n // 2-1)
            if n % 12 == 9:
                return '9'*(n//2)+'6'+'9'*(n//2)
            if n % 12 == 10:
                return '9' * (n // 2-1) + '44' + '9' * (n // 2-1)
            if n % 12 == 11:
                return '9'*(n//2)+'4'+'9'*(n//2)
        if k==8:
            return '888'+'9'*(n-6)+'888'
        if k==9:
            return '9' * n

        return ""
