import java.math.BigInteger;

class Solution {
    public String largestPalindrome(int n, int k) {
        if (n == 1) {
            if (k == 1 || k == 3 || k == 9)
                return "9";
            if (k == 2 || k == 4 || k == 8)
                return "8";
            return k + "";
        } else if (n == 2) {
            if (k == 1 || k == 3 || k == 9)
                return "99";
            if (k == 2 || k == 4 || k == 8)
                return "88";
            return k + "" + k;
        }
        if (k == 1 || k == 3 || k == 9)
            return "9".repeat(n);
        if (k == 2)
            return "8" + "9".repeat(n-2) + "8";
        if (k == 5)
            return "5" + "9".repeat(n-2) + "5";
        if (k == 4) {
            if (n <= 4)
                return "8".repeat(n);
            else
                return "88" + "9".repeat(n-4) + "88";
        }
        if (k == 8) {
            if (n <= 6)
                return "8".repeat(n);
            return "888" + "9".repeat(n-6) + "888";
        }
        if (k == 6) {
            if (n == 3)
                return "888";
            if (n == 4)
                return "8778";
            if (n % 2 == 0) {
                for (int i = 9; i >= 1; i--) {
                    if (new BigInteger("8" + "9".repeat(n/2-2) + i + "" + i + "9".repeat(n/2-2) + "8").mod(new BigInteger("6")).equals(BigInteger.ZERO))
                        return "8" + "9".repeat(n/2-2) + i + "" + i + "9".repeat(n/2-2) + "8";
                }
            } else {
                for (int i = 9; i >= 1; i--) {
                    if (new BigInteger("8" + "9".repeat(n/2-1) + i + "9".repeat(n/2-1) + "8").mod(new BigInteger("6")).equals(BigInteger.ZERO))
                        return "8" + "9".repeat(n/2-1) + i + "9".repeat(n/2-1) + "8";
                }
            }
        }
        if (k == 7) {
            if (n % 2 == 0) {
                for (int i = 9; i >= 1; i--) {
                    if (new BigInteger("9".repeat(n/2-1) + i + "" + i + "9".repeat(n/2-1)).mod(new BigInteger("7")).equals(BigInteger.ZERO))
                        return "9".repeat(n/2-1) + i + "" + i + "9".repeat(n/2-1);
                }
            } else {
                for (int i = 9; i >= 1; i--) {
                    if (new BigInteger("9".repeat(n/2) + i + "9".repeat(n/2)).mod(new BigInteger("7")).equals(BigInteger.ZERO))
                        return "9".repeat(n/2) + i + "9".repeat(n/2);
                }
            }
        }
        return "BAD";
    }
}
